/**
*<div id="function.mailparse-rfc822-parse-addresses" class="refentry">  <div class="refnamediv">   <h1 class="refname">mailparse_rfc822_parse_addresses</h1>   <p class="verinfo">(PECL mailparse &gt;= 0.9.0)</p><p class="refpurpose"><span class="refname">mailparse_rfc822_parse_addresses</span> &mdash; <span class="dc-title">Parse RFC 822 compliant addresses</span></p>   </div>  <div class="refsect1 description" id="refsect1-function.mailparse-rfc822-parse-addresses-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description">    <span class="type">array</span> <span class="methodname"><strong>mailparse_rfc822_parse_addresses</strong></span>     ( <span class="methodparam"><span class="type">string</span> <code class="parameter">$addresses</code></span>    )</div>    <p class="para rdfs-comment">    Parses a <a href="http://www.faqs.org/rfcs/rfc822" class="link external">&raquo;&nbsp;RFC 822</a> compliant recipient    list, such as that found in the <em>To:</em> header.   </p>  </div>    <div class="refsect1 parameters" id="refsect1-function.mailparse-rfc822-parse-addresses-parameters">   <h3 class="title">参数</h3>   <p class="para">    <dl>            <dt> <code class="parameter">addresses</code></dt>       <dd>        <p class="para">        A string containing addresses, like in:        <em>Wez Furlong &lt;wez@example.com&gt;, doe@example.com</em>       </p>       <blockquote class="note"><p><strong class="note">Note</strong>:         <p class="para">         This string must not include the header name.        </p>       </p></blockquote>      </dd>          </dl>    </p>  </div>    <div class="refsect1 returnvalues" id="refsect1-function.mailparse-rfc822-parse-addresses-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns an array of associative arrays with the following keys for each    recipient:    <table class="doctable informaltable">           <tbody class="tbody">       <tr>        <td><em>display</em></td>        <td>         The recipient name, for display purpose. If this part is not set for a         recipient, this key will hold the same value as          <em>address</em>.        </td>       </tr>        <tr>        <td><em>address</em></td>        <td>The email address</td>       </tr>        <tr>        <td><em>is_group</em></td>        <td><strong><code>TRUE</code></strong> if the recipient is a newsgroup, <strong><code>FALSE</code></strong> otherwise.</td>       </tr>       </tbody>         </table>    </p>  </div>    <div class="refsect1 examples" id="refsect1-function.mailparse-rfc822-parse-addresses-examples">   <h3 class="title">范例</h3>   <p class="para">    <div class="example" id="example-4044">     <p><strong>Example #1 <span class="function"><strong>mailparse_rfc822_parse_addresses()</strong></span> example</strong></p>     <div class="example-contents"> <div class="phpcode"><code><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /><br />$to&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'Wez&nbsp;Furlong&nbsp;&lt;wez@example.com&gt;,&nbsp;doe@example.com'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">mailparse_rfc822_parse_addresses</span><span style="color: #007700">(</span><span style="color: #9876AA">$to</span><span style="color: #007700">));<br /><br /></span><span style="color: #9876AA">?&gt;</span> </span> </code></div>     </div>      <div class="example-contents"><p>以上例程会输出：</p></div>     <div class="example-contents screen"> <div class="cdata"><pre> array(2) {   [0]=&gt;   array(3) {     [&quot;display&quot;]=&gt;     string(11) &quot;Wez Furlong&quot;     [&quot;address&quot;]=&gt;     string(15) &quot;wez@example.com&quot;     [&quot;is_group&quot;]=&gt;     bool(false)   }   [1]=&gt;   array(3) {     [&quot;display&quot;]=&gt;     string(15) &quot;doe@example.com&quot;     [&quot;address&quot;]=&gt;     string(15) &quot;doe@example.com&quot;     [&quot;is_group&quot;]=&gt;     bool(false)   } } </pre></div>     </div>    </div>   </p>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.mailparse.php">Mailparse 函数</a></div></div>
 * @return array <p>*/
function mailparse_rfc822_parse_addresses ($addresses) {}

/**
*<div id="function.mailparse-stream-encode" class="refentry">  <div class="refnamediv">   <h1 class="refname">mailparse_stream_encode</h1>   <p class="verinfo">(PECL mailparse &gt;= 0.9.0)</p><p class="refpurpose"><span class="refname">mailparse_stream_encode</span> &mdash; <span class="dc-title">    Streams data from source file pointer, apply encoding and write to destfp    </span></p>   </div>  <div class="refsect1 description" id="refsect1-function.mailparse-stream-encode-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description">    <span class="type">bool</span> <span class="methodname"><strong>mailparse_stream_encode</strong></span>     ( <span class="methodparam"><span class="type">resource</span> <code class="parameter">$sourcefp</code></span>    , <span class="methodparam"><span class="type">resource</span> <code class="parameter">$destfp</code></span>    , <span class="methodparam"><span class="type">string</span> <code class="parameter">$encoding</code></span>          )</div>    <p class="para rdfs-comment">    Streams data from the source file pointer, apply     <code class="parameter">encoding</code> and write to the destination file pointer.    </p>  </div>    <div class="refsect1 parameters" id="refsect1-function.mailparse-stream-encode-parameters">   <h3 class="title">参数</h3>   <p class="para">    <dl>            <dt> <code class="parameter">sourcefp</code></dt>       <dd>        <p class="para">        A valid file handle. The file is streamed through the parser.       </p>      </dd>                 <dt> <code class="parameter">destfp</code></dt>       <dd>        <p class="para">        The destination file handle in which the encoded data will be written.       </p>      </dd>                 <dt> <code class="parameter">encoding</code></dt>       <dd>        <p class="para">        One of the character encodings supported by the        <a href="http://php.net/manual/zh/ref.mbstring.php" class="link">mbstring</a> module.       </p>      </dd>          </dl>    </p>  </div>    <div class="refsect1 returnvalues" id="refsect1-function.mailparse-stream-encode-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><code>TRUE</code></strong>， 或者在失败时返回 <strong><code>FALSE</code></strong>。   </p>  </div>    <div class="refsect1 examples" id="refsect1-function.mailparse-stream-encode-examples">   <h3 class="title">范例</h3>   <p class="para">    <div class="example" id="imap-search.examples">     <p><strong>Example #1 <span class="function"><strong>mailparse_stream_encode()</strong></span> example</strong></p>     <div class="example-contents"> <div class="phpcode"><code><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">//&nbsp;email.eml&nbsp;contents:&nbsp;hello,&nbsp;this&nbsp;is&nbsp;some&nbsp;text=hello.<br /></span><span style="color: #9876AA">$fp&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">fopen</span><span style="color: #007700">(</span><span style="color: #DD0000">'email.eml'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'r'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$dest&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">tmpfile</span><span style="color: #007700">();<br /><br /></span><span style="color: #9876AA">mailparse_stream_encode</span><span style="color: #007700">(</span><span style="color: #9876AA">$fp</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$dest</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"quoted-printable"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">rewind</span><span style="color: #007700">(</span><span style="color: #9876AA">$dest</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;Display&nbsp;new&nbsp;file&nbsp;contents<br /></span><span style="color: #9876AA">fpassthru</span><span style="color: #007700">(</span><span style="color: #9876AA">$dest</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span> </span> </code></div>     </div>      <div class="example-contents"><p>以上例程会输出：</p></div>     <div class="example-contents screen"> <div class="cdata"><pre> hello, this is some text=3Dhello. </pre></div>     </div>    </div>   </p>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.mailparse.php">Mailparse 函数</a></div></div>
 * @return bool Returns <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function mailparse_stream_encode ($sourcefp, $destfp, $encoding) {}

/**
*<div id="function.mailparse-uudecode-all" class="refentry">  <div class="refnamediv">   <h1 class="refname">mailparse_uudecode_all</h1>   <p class="verinfo">(PECL mailparse &gt;= 0.9.0)</p><p class="refpurpose"><span class="refname">mailparse_uudecode_all</span> &mdash; <span class="dc-title">    Scans the data from fp and extract each embedded uuencoded file   </span></p>   </div>  <div class="refsect1 description" id="refsect1-function.mailparse-uudecode-all-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description">    <span class="type">array</span> <span class="methodname"><strong>mailparse_uudecode_all</strong></span>     ( <span class="methodparam"><span class="type">resource</span> <code class="parameter">$fp</code></span>    )</div>    <p class="para rdfs-comment">    Scans the data from the given file pointer and extract each embedded    uuencoded file into a temporary file.   </p>  </div>    <div class="refsect1 parameters" id="refsect1-function.mailparse-uudecode-all-parameters">   <h3 class="title">参数</h3>   <p class="para">    <dl>            <dt> <code class="parameter">fp</code></dt>       <dd>        <p class="para">        A valid file pointer.       </p>      </dd>          </dl>    </p>  </div>    <div class="refsect1 returnvalues" id="refsect1-function.mailparse-uudecode-all-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns an array of associative arrays listing filename information.    <table class="doctable informaltable">           <tbody class="tbody">       <tr>        <td><em>filename</em></td>        <td>Path to the temporary file name created</td>       </tr>        <tr>        <td><em>origfilename</em></td>        <td>The original filename, for uuencoded parts only</td>       </tr>       </tbody>         </table>     The first filename entry is the message body. The next entries are the    decoded uuencoded files.   </p>  </div>    <div class="refsect1 examples" id="refsect1-function.mailparse-uudecode-all-examples">   <h3 class="title">范例</h3>   <p class="para">    <div class="example" id="example-4046">     <p><strong>Example #1 <span class="function"><strong>mailparse_uudecode_all()</strong></span> example</strong></p>     <div class="example-contents"> <div class="phpcode"><code><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /><br />$text&nbsp;</span><span style="color: #007700">=&nbsp;&lt;&lt;&lt;EOD<br /></span><span style="color: #DD0000">To:&nbsp;fred@example.com<br /><br />hello,&nbsp;this&nbsp;is&nbsp;some&nbsp;text&nbsp;hello.<br />blah&nbsp;blah&nbsp;blah.<br /><br />begin&nbsp;644&nbsp;test.txt<br />/=&amp;AI&lt;R!I&lt;R!A('1E&lt;W0*<br />`<br />end<br /><br /></span><span style="color: #007700">EOD;<br /><br /></span><span style="color: #9876AA">$fp&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">tmpfile</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">fwrite</span><span style="color: #007700">(</span><span style="color: #9876AA">$fp</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$text</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$data&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">mailparse_uudecode_all</span><span style="color: #007700">(</span><span style="color: #9876AA">$fp</span><span style="color: #007700">);<br /><br />echo&nbsp;</span><span style="color: #DD0000">"BODY\n"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">readfile</span><span style="color: #007700">(</span><span style="color: #9876AA">$data</span><span style="color: #007700">[</span><span style="color: #9876AA">0</span><span style="color: #007700">][</span><span style="color: #DD0000">"filename"</span><span style="color: #007700">]);<br />echo&nbsp;</span><span style="color: #DD0000">"UUE&nbsp;(</span><span style="color: #007700">{</span><span style="color: #9876AA">$data</span><span style="color: #007700">[</span><span style="color: #9876AA">1</span><span style="color: #007700">][</span><span style="color: #DD0000">'origfilename'</span><span style="color: #007700">]}</span><span style="color: #DD0000">)\n"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">readfile</span><span style="color: #007700">(</span><span style="color: #9876AA">$data</span><span style="color: #007700">[</span><span style="color: #9876AA">1</span><span style="color: #007700">][</span><span style="color: #DD0000">"filename"</span><span style="color: #007700">]);<br /><br /></span><span style="color: #FF8000">//&nbsp;Clean&nbsp;up<br /></span><span style="color: #9876AA">unlink</span><span style="color: #007700">(</span><span style="color: #9876AA">$data</span><span style="color: #007700">[</span><span style="color: #9876AA">0</span><span style="color: #007700">][</span><span style="color: #DD0000">"filename"</span><span style="color: #007700">]);<br /></span><span style="color: #9876AA">unlink</span><span style="color: #007700">(</span><span style="color: #9876AA">$data</span><span style="color: #007700">[</span><span style="color: #9876AA">1</span><span style="color: #007700">][</span><span style="color: #DD0000">"filename"</span><span style="color: #007700">]);<br /><br /></span><span style="color: #9876AA">?&gt;</span> </span> </code></div>     </div>      <div class="example-contents"><p>以上例程会输出：</p></div>     <div class="example-contents screen"> <div class="cdata"><pre> BODY To: fred@example.com  hello, this is some text hello. blah blah blah.  UUE (test.txt) this is a test </pre></div>     </div>    </div>   </p>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.mailparse.php">Mailparse 函数</a></div></div>
 * @return array <p>*/
function mailparse_uudecode_all ($fp) {}

