define ('NIL', 0);
define ('IMAP_OPENTIMEOUT', 1);
define ('IMAP_READTIMEOUT', 2);
define ('IMAP_WRITETIMEOUT', 3);
define ('IMAP_CLOSETIMEOUT', 4);
define ('OP_DEBUG', 1);

/**
*      Open mailbox read-only     
*/
define ('OP_READONLY', 2);

/**
*      Don&#039;t use or update a <var class="filename">.newsrc</var> for news       (NNTP only)     
*/
define ('OP_ANONYMOUS', 4);
define ('OP_SHORTCACHE', 8);
define ('OP_SILENT', 16);
define ('OP_PROTOTYPE', 32);

/**
*      For <acronym title="Internet Message Access Protocol">IMAP</acronym> and <acronym title="Network News Transfer Protocol">NNTP</acronym>      names, open a connection but don&#039;t open a mailbox.     
*/
define ('OP_HALFOPEN', 64);
define ('OP_EXPUNGE', 128);
define ('OP_SECURE', 256);

/**
*      silently expunge the mailbox before closing when      calling <span class="function">{@link imap_close()}</span>     
*/
define ('CL_EXPUNGE', 32768);

/**
*      The parameter is a UID     
*/
define ('FT_UID', 1);

/**
*      Do not set the \Seen flag if not already set     
*/
define ('FT_PEEK', 2);
define ('FT_NOT', 4);

/**
*      The return string is in internal format, will not canonicalize to CRLF.     
*/
define ('FT_INTERNAL', 8);
define ('FT_PREFETCHTEXT', 32);

/**
*      The sequence argument contains UIDs instead of sequence numbers     
*/
define ('ST_UID', 1);
define ('ST_SILENT', 2);
define ('ST_SET', 4);

/**
*      the sequence numbers contain UIDS     
*/
define ('CP_UID', 1);

/**
*      Delete the messages from the current mailbox after copying      with <span class="function">{@link imap_mail_copy()}</span>     
*/
define ('CP_MOVE', 2);

/**
*      Return UIDs instead of sequence numbers     
*/
define ('SE_UID', 1);
define ('SE_FREE', 2);

/**
*      Don&#039;t prefetch searched messages     
*/
define ('SE_NOPREFETCH', 4);
define ('SO_FREE', 8);
define ('SO_NOSERVER', 16);
define ('SA_MESSAGES', 1);
define ('SA_RECENT', 2);
define ('SA_UNSEEN', 4);
define ('SA_UIDNEXT', 8);
define ('SA_UIDVALIDITY', 16);
define ('SA_ALL', 31);

/**
*      This mailbox has no &quot;children&quot; (there are no      mailboxes below this one).     
*/
define ('LATT_NOINFERIORS', 1);

/**
*      This is only a container, not a mailbox - you      cannot open it.     
*/
define ('LATT_NOSELECT', 2);

/**
*      This mailbox is marked. Only used by UW-IMAPD.     
*/
define ('LATT_MARKED', 4);

/**
*      This mailbox is not marked. Only used by      UW-IMAPD.     
*/
define ('LATT_UNMARKED', 8);
/**
*      This container has a referral to a remote mailbox.     
*/
define ('LATT_REFERRAL', 16);
/**
*      This mailbox has selectable inferiors.     
*/
define ('LATT_HASCHILDREN', 32);
/**
*      This mailbox has no selectable inferiors.     
*/
define ('LATT_HASNOCHILDREN', 64);

/**
*      Sort criteria for <span class="function">{@link imap_sort()}</span>:      message Date     
*/
define ('SORTDATE', 0);

/**
*      Sort criteria for <span class="function">{@link imap_sort()}</span>:      arrival date     
*/
define ('SORTARRIVAL', 1);

/**
*      Sort criteria for <span class="function">{@link imap_sort()}</span>:      mailbox in first From address     
*/
define ('SORTFROM', 2);

/**
*      Sort criteria for <span class="function">{@link imap_sort()}</span>:      message subject     
*/
define ('SORTSUBJECT', 3);

/**
*      Sort criteria for <span class="function">{@link imap_sort()}</span>:      mailbox in first To address     
*/
define ('SORTTO', 4);

/**
*      Sort criteria for <span class="function">{@link imap_sort()}</span>:      mailbox in first cc address     
*/
define ('SORTCC', 5);

/**
*      Sort criteria for <span class="function">{@link imap_sort()}</span>:      size of message in octets     
*/
define ('SORTSIZE', 6);
/**
*      Primary body type: unformatted text     
*/
define ('TYPETEXT', 0);
/**
