function pspell_check ($dictionary_link, $word) {}

/**
*<div id="function.pspell-suggest" class="refentry">  <div class="refnamediv">   <h1 class="refname">pspell_suggest</h1>   <p class="verinfo">(PHP 4 &gt;= 4.0.2, PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">pspell_suggest</span> &mdash; <span class="dc-title">Suggest spellings of a word</span></p>   </div>    <div class="refsect1 description" id="refsect1-function.pspell-suggest-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description">    <span class="type">array</span> <span class="methodname"><strong>pspell_suggest</strong></span>     ( <span class="methodparam"><span class="type">int</span> <code class="parameter">$dictionary_link</code></span>    , <span class="methodparam"><span class="type">string</span> <code class="parameter">$word</code></span>    )</div>    <p class="simpara">    <span class="function"><strong>pspell_suggest()</strong></span> returns an array of possible    spellings for the given word.   </p>  </div>    <div class="refsect1 parameters" id="refsect1-function.pspell-suggest-parameters">   <h3 class="title">参数</h3>   <p class="para">    <dl>            <dt> <code class="parameter">dictionary_link</code></dt>       <dd>        <p class="para">       </p>      </dd>                 <dt> <code class="parameter">word</code></dt>       <dd>        <p class="para">        The tested word.       </p>      </dd>          </dl>    </p>  </div>    <div class="refsect1 returnvalues" id="refsect1-function.pspell-suggest-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns an array of possible spellings.   </p>  </div>    <div class="refsect1 examples" id="refsect1-function.pspell-suggest-examples">   <h3 class="title">范例</h3>   <p class="para">    <div class="example" id="example-3209">     <p><strong>Example #1 <span class="function"><strong>pspell_suggest()</strong></span> example</strong></p>     <div class="example-contents"> <div class="phpcode"><code><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$pspell_link&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">pspell_new</span><span style="color: #007700">(</span><span style="color: #DD0000">"en"</span><span style="color: #007700">);<br /><br />if&nbsp;(!</span><span style="color: #9876AA">pspell_check</span><span style="color: #007700">(</span><span style="color: #9876AA">$pspell_link</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"testt"</span><span style="color: #007700">))&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$suggestions&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">pspell_suggest</span><span style="color: #007700">(</span><span style="color: #9876AA">$pspell_link</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"testt"</span><span style="color: #007700">);<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;foreach&nbsp;(</span><span style="color: #9876AA">$suggestions&nbsp;</span><span style="color: #007700">as&nbsp;</span><span style="color: #9876AA">$suggestion</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"Possible&nbsp;spelling:&nbsp;</span><span style="color: #9876AA">$suggestion</span><span style="color: #DD0000">&lt;br&nbsp;/&gt;"</span><span style="color: #007700">;<br />&nbsp;&nbsp;&nbsp;&nbsp;}<br />}<br /></span><span style="color: #9876AA">?&gt;</span> </span> </code></div>     </div>     </div>   </p>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.pspell.php">Pspell 函数</a></div></div>
