span class="type" style="color:#EAB766">void</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/domnode.normalize.php" class="methodname" style="color:#CC7832">normalize</a></span>     ( <span class="methodparam">void</span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span> <span class="type" style="color:#EAB766">DOMNode</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/domnode.removechild.php" class="methodname" style="color:#CC7832">removeChild</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span> <span class="parameter" style="color:#2EACF9">$oldnode</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span> <span class="type" style="color:#EAB766">DOMNode</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/domnode.replacechild.php" class="methodname" style="color:#CC7832">replaceChild</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span> <span class="parameter" style="color:#2EACF9">$newnode</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span> <span class="parameter" style="color:#2EACF9">$oldnode</span></span>    )</div>     }</div>      </div>      <div class="section" id="domnode.props">    <h2 class="title">属性</h2>    <dl>            <dt id="domnode.props.nodename"><var class="varname"><var class="varname">nodeName</var></var></dt>       <dd>        <p class="para">Returns the most accurate name for the current node type</p>      </dd>                 <dt id="domnode.props.nodevalue"><var class="varname"><var class="varname">nodeValue</var></var></dt>       <dd>        <p class="para">        The value of this node, depending on its type.        Contrary to the W3C specification, the node value of        <a href="http://php.net/manual/zh/class.domelement.php" class="classname">DOMElement</a> nodes is equal to {@link DOMNode::textContent} instead        of <strong><span>NULL</span></strong>.       </p>      </dd>                 <dt id="domnode.props.nodetype"><var class="varname"><var class="varname">nodeType</var></var></dt>       <dd>        <p class="para">Gets the type of the node. One of the predefined <a href="http://php.net/manual/zh/dom.constants.php" class="link">XML_xxx_NODE constants</a></p>      </dd>                 <dt id="domnode.props.parentnode"><var class="varname"><var class="varname">parentNode</var></var></dt>       <dd>        <p class="para">The parent of this node. If there is no such node, this returns <strong><span>NULL</span></strong>.</p>      </dd>                 <dt id="domnode.props.childnodes"><var class="varname"><var class="varname">childNodes</var></var></dt>       <dd>        <p class="para">        A <a href="http://php.net/manual/zh/class.domnodelist.php" class="classname">DOMNodeList</a> that contains all        children of this node. If there are no children, this is an empty        <a href="http://php.net/manual/zh/class.domnodelist.php" class="classname">DOMNodeList</a>.       </p>      </dd>                 <dt id="domnode.props.firstchild"><var class="varname"><var class="varname">firstChild</var></var></dt>       <dd>        <p class="para">        The first child of this node. If there is no such node, this        returns <strong><span>NULL</span></strong>.       </p>      </dd>                 <dt id="domnode.props.lastchild"><var class="varname"><var class="varname">lastChild</var></var></dt>       <dd>        <p class="para">The last child of this node. If there is no such node, this returns <strong><span>NULL</span></strong>.</p>      </dd>                 <dt id="domnode.props.previoussibling"><var class="varname"><var class="varname">previousSibling</var></var></dt>       <dd>        <p class="para">        The node immediately preceding this node. If there is no such        node, this returns <strong><span>NULL</span></strong>.       </p>      </dd>                 <dt id="domnode.props.nextsibling"><var class="varname"><var class="varname">nextSibling</var></var></dt>       <dd>        <p class="para">        The node immediately following this node. If there is no such        node, this returns <strong><span>NULL</span></strong>.       </p>      </dd>                 <dt id="domnode.props.attributes"><var class="varname"><var class="varname">attributes</var></var></dt>       <dd>        <p class="para">        A <a href="http://php.net/manual/zh/class.domnamednodemap.php" class="classname">DOMNamedNodeMap</a> containing the        attributes of this node (if it is a <a href="http://php.net/manual/zh/class.domelement.php" class="classname">DOMElement</a>)        or <strong><span>NULL</span></strong> otherwise.       </p>      </dd>                 <dt id="domnode.props.ownerdocument"><var class="varname"><var class="varname">ownerDocument</var></var></dt>       <dd>        <p class="para">The <a href="http://php.net/manual/zh/class.domdocument.php" class="classname">DOMDocument</a> object associated with this node.</p>      </dd>                 <dt id="domnode.props.namespaceuri"><var class="varname"><var class="varname">namespaceURI</var></var></dt>       <dd>        <p class="para">The namespace URI of this node, or <strong><span>NULL</span></strong> if it is unspecified.</p>      </dd>                 <dt id="domnode.props.prefix"><var class="varname"><var class="varname">prefix</var></var></dt>       <dd>        <p class="para">The namespace prefix of this node, or <strong><span>NULL</span></strong> if it is unspecified.</p>      </dd>                 <dt id="domnode.props.localname"><var class="varname"><var class="varname">localName</var></var></dt>       <dd>        <p class="para">Returns the local part of the qualified name of this node.</p>      </dd>                 <dt id="domnode.props.baseuri"><var class="varname"><var class="varname">baseURI</var></var></dt>       <dd>        <p class="para">        The absolute base URI of this node or <strong><span>NULL</span></strong> if the implementation        wasn&#039;t able to obtain an absolute URI.       </p>      </dd>                 <dt id="domnode.props.textcontent"><var class="varname"><var class="varname">textContent</var></var></dt>       <dd>        <p class="para">The text content of this node and its descendants.</p>      </dd>          </dl>    </div>      <div class="section">    <h2 class="title">注释</h2>    <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>:      <p class="para">      The DOM extension uses UTF-8 encoding. Use <span class="function">{@link utf8_encode()}</span>      and <span class="function">{@link utf8_decode()}</span> to work with texts in ISO-8859-1      encoding or <a href="http://php.net/manual/zh/ref.iconv.php" class="link">Iconv</a> for other encodings.     </p>    </p></blockquote>   </div>    <div class="section">   <h2 class="title">更新日志</h2>   <p class="para">    <table class="doctable informaltable">           <thead>       <tr>        <th>版本</th>        <th>说明</th>       </tr>       </thead>       <tbody class="tbody">       <tr>        <td>5.6.1</td>        <td>         The textContent property has been made writable (formerly it has been         readonly).        </td>       </tr>       </tbody>         </table>    </p>  </div>     <div class="section">    <h2 class="title">参见</h2>    <p class="para">     <ul class="simplelist">      <li class="member"><a href="http://www.w3.org/TR/2003/WD-DOM-Level-3-Core-20030226/DOM3-Core.html#core-ID-1950641247" class="link external">&raquo;&nbsp;W3C specification of Node</a></li>     </ul>    </p>   </div>     </div>                                                                                                                 <h2>Table of Contents</h2><ul class="chunklist chunklist_reference"><li>{@link DOMNode::appendChild} — Adds new child at the end of the children</li><li>{@link DOMNode::C14N} — Canonicalize nodes to a string</li><li>{@link DOMNode::C14NFile} — Canonicalize nodes to a file</li><li>{@link DOMNode::cloneNode} — Clones a node</li><li>{@link DOMNode::getLineNo} — Get line number for a node</li><li>{@link DOMNode::getNodePath} — Get an XPath for a node</li><li>{@link DOMNode::hasAttributes} — Checks if node has attributes</li><li>{@link DOMNode::hasChildNodes} — Checks if node has children</li><li>{@link DOMNode::insertBefore} — Adds a new child before a reference node</li><li>{@link DOMNode::isDefaultNamespace} — Checks if the specified namespaceURI is the default namespace or not</li><li>{@link DOMNode::isSameNode} — Indicates if two nodes are the same node</li><li>{@link DOMNode::isSupported} — Checks if feature is supported for specified version</li><li>{@link DOMNode::lookupNamespaceUri} — Gets the namespace URI of the node based on the prefix</li><li>{@link DOMNode::lookupPrefix} — Gets the namespace prefix of the node based on the namespace URI</li><li>{@link DOMNode::normalize} — Normalizes the node</li><li>{@link DOMNode::removeChild} — Removes child from list of children</li><li>{@link DOMNode::replaceChild} — Replaces a child</li></ul> <div class="up"><a href="http://php.net/manual/zh/book.dom.php">DOM</a></div></div>
*/
class DOMNode  {

    /**
     * @var string
     * @since 5.0
     * Returns the most accurate name for the current node type
     * @link http://php.net/manual/en/class.domnode.php#domnode.props.nodename
     */
    public $nodeName;

    /**
     * @var string
     * @since 5.0
     * The value of this node, depending on its type
     * @link http://php.net/manual/en/class.domnode.php#domnode.props.nodevalue
     */
    public $nodeValue;

    /**
     * @var int
     * @since 5.0
     * Gets the type of the node. One of the predefined
     * <a href="http://www.php.net/manual/en/dom.constants.php">XML_xxx_NODE</a> constants
     * @link http://php.net/manual/en/class.domnode.php#domnode.props.nodetype
     */
    public $nodeType;

    /**
     * @var DOMNode
     * @since 5.0
