<?php

// Start of fileinfo v.1.0.5

/**
*<div id="class.finfo" class="reference">   <h1 class="title">finfo 类</h1>     <div class="partintro"><p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7, PECL fileinfo &gt;= 0.1.0)</p>     <div class="section" id="finfo.intro">    <h2 class="title">简介</h2>    <p class="para">     该类在 fileinfo 函数集中提供了一个面向对象的接口。    </p>   </div>     <div class="section" id="finfo.synopsis">    <h2 class="title">类摘要</h2>      <div class="classsynopsis">     <div class="ooclass"></div>       <div class="classsynopsisinfo">      <span class="ooclass">       <strong class="classname">finfo</strong>      </span>      {</div>           <div class="constructorsynopsis dc-description">    <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/finfo.construct.php" class="methodname" style="color:#CC7832">__construct</a></span>     ([ <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span><span class="initializer"> = FILEINFO_NONE</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$magic_file</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>   ]] )</div>      <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span> <span class="type" style="color:#EAB766">string</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/finfo.buffer.php" class="methodname" style="color:#CC7832">buffer</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$string</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span><span class="initializer"> = FILEINFO_NONE</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$context</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>   ]] )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span> <span class="type" style="color:#EAB766">string</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/finfo.file.php" class="methodname" style="color:#CC7832">file</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$file_name</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span><span class="initializer"> = FILEINFO_NONE</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$context</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>   ]] )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span> <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/finfo.set-flags.php" class="methodname" style="color:#CC7832">set_flags</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span></span>    )</div>     }</div>     </div>   </div>                                 <h2>Table of Contents</h2><ul class="chunklist chunklist_reference"><li>{@link finfo::buffer} — 别名 finfo_buffer()</li><li>{@link finfo::__construct} — 别名 finfo_open</li><li>{@link finfo::file} — 别名 finfo_file()</li><li>{@link finfo::set_flags} — 别名 finfo_set_flags()</li></ul> <div class="up"><a href="http://php.net/manual/zh/book.fileinfo.php">Fileinfo</a></div></div>
*/
class finfo  {

	/**
	 * @param $options [optional]
	 * @param $arg [optional]
	 */
	public function finfo ($options, $arg) {}

	/**
	 * (PHP &gt;= 5.3.0, PECL fileinfo &gt;= 0.1.0)<br/>
	 * Set libmagic configuration options
	 * @link http://php.net/manual/en/function.finfo-set-flags.php
	 * @param int $options <p>
	 * One or disjunction of more Fileinfo
	 * constants.
	 * </p>
	 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.
	 */
	public function set_flags ($options) {}

	/**
	 * (PHP &gt;= 5.3.0, PECL fileinfo &gt;= 0.1.0)<br/>
	 * Return information about a file
	 * @link http://php.net/manual/en/function.finfo-file.php
	 * @param string $file_name [optional] <p>
	 * Name of a file to be checked.
	 * </p>
	 * @param int $options [optional] <p>
	 * One or disjunction of more Fileinfo
	 * constants.
	 * </p>
	 * @param resource $context [optional] <p>
	 * For a description of contexts, refer to .
	 * </p>
	 * @return string a textual description of the contents of the
	 * <i>filename</i> argument, or <b>FALSE</b> if an error occurred.
	 */
/**
*<div id="function.file" class="refentry">  <div class="refnamediv">   <h1 class="refname">file</h1>   <p class="verinfo">(PHP 4, PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">file</span> &mdash; <span class="dc-title">把整个文件读入一个数组中</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.file-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">array</span> <span class="methodname" style="color:#CC7832"><strong>file</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$filename</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$flags</span><span class="initializer"> = 0</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$context</span></span>   ]] )</div>    <p class="para rdfs-comment">    把整个文件读入一个数组中。   </p>     <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>:     <p class="para">     你可以通过  <span class="function">{@link file_get_contents()}</span>  以字符串形式获取文件的内容。    </p>   </p></blockquote>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.file-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">filename</span></dt>       <dd>        <p class="para">        文件的路径。       </p>       <div class="tip"><strong class="tip">Tip</strong><p class="simpara">如已启用<a href="http://php.net/manual/zh/filesystem.configuration.php#ini.allow-url-fopen" class="link">fopen 包装器</a>，在此函数中， URL 可作为文件名。关于如何指定文件名详见 <span class="function">{@link fopen()}</span>。各种 wapper 的不同功能请参见 <a href="http://php.net/manual/zh/wrappers.php" class="xref">支持的协议和封装协议</a>，注意其用法及其可提供的预定义变量。</p></div>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">flags</span></dt>       <dd>        <p class="para">        可选参数 <span class="parameter" style="color:#2EACF9">flags</span> 可以是以下一个或多个常量：        <dl>                    <dt>            <strong><span>FILE_USE_INCLUDE_PATH</span></strong>          </dt>           <dd>            <span class="simpara">            在 <a href="http://php.net/manual/zh/ini.core.php#ini.include-path" class="link">include_path</a> 中查找文件。           </span>          </dd>                             <dt>            <strong><span>FILE_IGNORE_NEW_LINES</span></strong>          </dt>           <dd>            <span class="simpara">            在数组每个元素的末尾不要添加换行符           </span>          </dd>                             <dt>            <strong><span>FILE_SKIP_EMPTY_LINES</span></strong>          </dt>           <dd>            <span class="simpara">            跳过空行           </span>          </dd>                           </dl>        </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">context</span></dt>       <dd>        <p class="para">        A context resource created with the         <span class="function">{@link stream_context_create()}</span> function.       </p>       <p class="para">        <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>: <span class="simpara">在 PHP 5.0.0 中增加了对上下文（Context）的支持。有关<em>上下文（Context）</em>的说明参见 <a href="http://php.net/manual/zh/book.stream.php" class="xref">Streams</a>。</span></p></blockquote>       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.file-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns the file in an array. Each element of the array corresponds to a    line in the file, with the newline still attached. Upon failure,    <span class="function"><strong style="color:#CC7832">file()</strong></span> returns <strong><span>FALSE</span></strong>.   </p>   <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>:     <p class="para">     Each line in the resulting array will include the line ending, unless     <strong><span>FILE_IGNORE_NEW_LINES</span></strong> is used, so you still need to     use <span class="function">{@link rtrim()}</span> if you do not want the line ending     present.    </p>   </p></blockquote>   <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>: <span class="simpara">在读取在 Macintosh 电脑中或由其创建的文件时， 如果 PHP 不能正确的识别行结束符，启用运行时配置可选项 <a href="http://php.net/manual/zh/filesystem.configuration.php#ini.auto-detect-line-endings" class="link">auto_detect_line_endings</a> 也许可以解决此问题。</span></p></blockquote>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.file-changelog">   <h3 class="title">更新日志</h3>   <span>    <table class="doctable informaltable">           <thead>       <tr>        <th>版本</th>        <th>说明</th>       </tr>       </thead>       <tbody class="tbody">              <tr>        <td>5.0.0</td>        <td>         增加了参数  <span class="parameter" style="color:#2EACF9">context</span>        </td>       </tr>        <tr>        <td>5.0.0</td>        <td>         Prior to PHP 5.0.0 the <span class="parameter" style="color:#2EACF9">flags</span> parameter only         covered <a href="http://php.net/manual/zh/ini.core.php#ini.include-path" class="link">include_path</a> and was         enabled with 1        </td>       </tr>        <tr>        <td>4.3.0</td>        <td>         <span class="function"><strong style="color:#CC7832">file()</strong></span> 开始是二进制安全的        </td>       </tr>       </tbody>         </table>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.file-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-2780">     <p><strong>Example #1 <span class="function"><strong style="color:#CC7832">file()</strong></span> 例子</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">//&nbsp;将一个文件读入数组。本例中通过&nbsp;HTTP&nbsp;从&nbsp;URL&nbsp;中取得&nbsp;HTML&nbsp;源文件。<br /><br /></span><span style="color: #9876AA">$lines&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">file</span><span style="color: #007700">(</span><span style="color: #DD0000">'http://www.example.com/'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;在数组中循环，显示&nbsp;HTML&nbsp;的源文件并加上行号。<br /><br /></span><span style="color: #007700">foreach&nbsp;(</span><span style="color: #9876AA">$lines&nbsp;</span><span style="color: #007700">as&nbsp;</span><span style="color: #9876AA">$line_num&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">$line</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"Line&nbsp;#&lt;b&gt;</span><span style="color: #007700">{</span><span style="color: #9876AA">$line_num</span><span style="color: #007700">}</span><span style="color: #DD0000">&lt;/b&gt;&nbsp;:&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">htmlspecialchars</span><span style="color: #007700">(</span><span style="color: #9876AA">$line</span><span style="color: #007700">)&nbsp;.&nbsp;</span><span style="color: #DD0000">"&lt;br&nbsp;/&gt;\n"</span><span style="color: #007700">;<br />}<br /><br /></span><span style="color: #FF8000">//&nbsp;另一个例子将&nbsp;web&nbsp;页面读入字符串。参见&nbsp;file_get_contents()。<br /><br /></span><span style="color: #9876AA">$html&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">implode</span><span style="color: #007700">(</span><span style="color: #DD0000">''</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">file</span><span style="color: #007700">(</span><span style="color: #DD0000">'http://www.example.com/'</span><span style="color: #007700">));<br /><br /></span><span style="color: #FF8000">//&nbsp;从&nbsp;PHP&nbsp;5&nbsp;开始可以使用可选标记参数<br /></span><span style="color: #9876AA">$trimmed&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">file</span><span style="color: #007700">(</span><span style="color: #DD0000">'somefile.txt'</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">FILE_IGNORE_NEW_LINES&nbsp;</span><span style="color: #007700">|&nbsp;</span><span style="color: #9876AA">FILE_SKIP_EMPTY_LINES</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.file-notes">   <h3 class="title">注释</h3>   <div class="warning"><strong class="warning">Warning</strong><p class="para">使用 SSL 时，Microsoft IIS 会违反协议不发送<em>close_notify</em>标记就关闭连接。PHP 会在到达数据尾端时报告“SSL: Fatal Protocol Error”。 要解决此问题，<a href="http://php.net/manual/zh/errorfunc.configuration.php#ini.error-reporting" class="link">error_reporting</a> 应设定为降低级别至不包含警告。 PHP 4.3.7 及更高版本可以在使用 <em>https://</em> 包装器打开流时检测出有问题的 IIS 服务器软件 并抑制警告。在使用 <span class="function">{@link fsockopen()}</span> 创建 <em>ssl://</em> 套接字时, 开发者需检测并抑制此警告。</p></div>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.file-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link readfile()} - 输出文件</span></li>     <li class="member"><span class="function">{@link fopen()} - 打开文件或者 URL</span></li>     <li class="member"><span class="function">{@link fsockopen()} - 打开一个网络连接或者一个Unix套接字连接</span></li>     <li class="member"><span class="function">{@link popen()} - 打开进程文件指针</span></li>     <li class="member"><span class="function">{@link file_get_contents()} - 将整个文件读入一个字符串</span></li>     <li class="member"><span class="function">{@link include} - include</span></li>     <li class="member"><span class="function">{@link stream_context_create()} - 创建资源流上下文</span></li>    </ul>   </span>  </div>  <div class="up"><a href="http://php.net/manual/zh/ref.filesystem.php">文件系统函数</a></div></div>
*/
	public function file ($file_name = null, $options = FILEINFO_NONE, $context = null) {}

	/**
	 * (PHP 5 &gt;= 5.3.0, PECL fileinfo &gt;= 0.1.0)<br/>
	 * Return information about a string buffer
	 * @link http://php.net/manual/en/function.finfo-buffer.php
	 * @param string $string [optional] <p>
	 * Content of a file to be checked.
	 * </p>
	 * @param int $options [optional] <p>
	 * One or disjunction of more Fileinfo
	 * constants.
	 * </p>
	 * @param resource $context [optional]
	 * @return string a textual description of the <i>string</i>
	 * argument, or <b>FALSE</b> if an error occurred.
	 */
	public function buffer ($string = null, $options = FILEINFO_NONE, $context = null) {}

}

/**
*<div id="function.finfo-open" class="refentry">  <div class="refnamediv">   <h1 class="refname">finfo_open</h1>   <h1 class="refname">finfo::__construct</h1>   <p class="verinfo">(PHP &gt;= 5.3.0, PECL fileinfo &gt;= 0.1.0)</p><p class="refpurpose"><span class="refname">finfo_open</span> -- <span class="refname">finfo::__construct</span> &mdash; <span class="dc-title">创建一个 fileinfo 资源</span></p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.finfo-open-description">   <h3 class="title">说明</h3>   <p class="para">过程化风格</p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">resource</span> <span class="methodname" style="color:#CC7832"><strong>finfo_open</strong></span>     ([ <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span><span class="initializer"> = FILEINFO_NONE</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$magic_file</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>   ]] )</div>    <p class="para rdfs-comment">面向对象风格 （构造器）：</p>   <div class="constructorsynopsis dc-description">    <span class="modifier">public</span> <span class="methodname" style="color:#CC7832">{@link finfo::__construct}</span>     ([ <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span><span class="initializer"> = FILEINFO_NONE</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$magic_file</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>   ]] )</div>    <p class="para rdfs-comment">     本函数打开一个魔数数据库并且返回它的资源。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.finfo-open-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">options</span></dt>       <dd>        <p class="para">        一个 <a href="http://php.net/manual/zh/fileinfo.constants.php" class="link">Fileinfo        常量 </a> 或多个 <a href="http://php.net/manual/zh/fileinfo.constants.php" class="link">Fileinfo 常量 </a> 进行逻辑或运算。       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">magic_file</span></dt>       <dd>        <p class="para">         魔数数据库文件名称，         通常是 <var class="filename">/path/to/magic.mime</var>。         如果未指定，则使用 <em>MAGIC</em> 环境变量。         如果未指定此环境变量，         则使用 PHP 绑定的魔数数据库。       </p>       <p class="para">        传入 <strong><span>NULL</span></strong> 或者空字符串，等同于使用默认值。       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.finfo-open-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    （仅适用于过程化风格）    如果成功则返回一个表示魔数数据库的资源，     或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.finfo-open-notes">   <h3 class="title">注释</h3>   <div class="warning"><strong class="warning">Warning</strong>    <p class="para">     在 PHP 5.3.11 和 5.4.1 中预期的魔数数据库格式发生了变动，     所以，内置的魔数数据库被更新。     如果使用了外部魔数数据库，     可能会由于格式不同导致读取失败。          同时，一些 mime 类型的文字表示也发生了变化，     例如，PHP 文件的 mime 类型由 “&quot;PHP script text”     变为“PHP script, ASCII text”。    </p>   </div>   <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>:     <p class="para">     通常来说，使用 PHP 绑定的魔数数据库（设置 <span class="parameter" style="color:#2EACF9">magic_file</span> 参数为空，     不设置 <em>MAGIC</em> 环境变量）是最好的选择，     除非你确实需要一个自定义的魔数数据库。    </p>   </p></blockquote>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.finfo-open-examples">   <h3 class="title">范例</h3>   <div class="example" id="example-2754">    <p><strong>Example #1 面向对象风格</strong></p>    <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$finfo&nbsp;</span><span style="color: #007700">=&nbsp;new&nbsp;</span><span style="color: #9876AA">finfo</span><span style="color: #007700">(</span><span style="color: #9876AA">FILEINFO_MIME</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"/usr/share/misc/magic"</span><span style="color: #007700">);&nbsp;</span><span style="color: #FF8000">//&nbsp;返回&nbsp;mime&nbsp;类型<br /><br />//&nbsp;get&nbsp;mime-type&nbsp;for&nbsp;a&nbsp;specific&nbsp;file&nbsp;<br /></span><span style="color: #9876AA">$filename&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">"/usr/local/something.txt"</span><span style="color: #007700">;<br />echo&nbsp;</span><span style="color: #9876AA">$finfo</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">file</span><span style="color: #007700">(</span><span style="color: #9876AA">$filename</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>   </div>    </div>   <div class="example" id="example-2755">    <p><strong>Example #2 过程化风格</strong></p>    <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$finfo&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">finfo_open</span><span style="color: #007700">(</span><span style="color: #9876AA">FILEINFO_MIME</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"/usr/share/misc/magic"</span><span style="color: #007700">);&nbsp;</span><span style="color: #FF8000">//&nbsp;返回&nbsp;mime&nbsp;类型<br /><br /></span><span style="color: #007700">if&nbsp;(!</span><span style="color: #9876AA">$finfo</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"Opening&nbsp;fileinfo&nbsp;database&nbsp;failed"</span><span style="color: #007700">;<br />&nbsp;&nbsp;&nbsp;&nbsp;exit();<br />}<br /><br /></span><span style="color: #FF8000">//&nbsp;获取指定文件的&nbsp;mime&nbsp;类型&nbsp;<br /></span><span style="color: #9876AA">$filename&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">"/usr/local/something.txt"</span><span style="color: #007700">;<br />echo&nbsp;</span><span style="color: #9876AA">finfo_file</span><span style="color: #007700">(</span><span style="color: #9876AA">$finfo</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$filename</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;关闭资源&nbsp;<br /></span><span style="color: #9876AA">finfo_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$finfo</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>    </div>    </div>   <span>以上例程会输出：</span>   <div class="example-contents screen" style="background:black;padding-left:5px;"> <div class="cdata"><span> text/plain; charset=us-ascii </span></div>   </div>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.finfo-open-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link finfo_close()} - 关闭 fileinfo 资源</span></li>    </ul>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.fileinfo.php">Fileinfo 函数</a></div></div>
 * @return resource a magic database resource on success or <b>FALSE</b> on failure.*/
function finfo_open ($options = null, $magic_file = null) {}

/**
*<div id="function.finfo-close" class="refentry">  <div class="refnamediv">   <h1 class="refname">finfo_close</h1>   <p class="verinfo">(PHP &gt;= 5.3.0, PECL fileinfo &gt;= 0.1.0)</p><p class="refpurpose"><span class="refname">finfo_close</span> &mdash; <span class="dc-title">关闭 fileinfo 资源</span></p>   </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.finfo-close-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><strong>finfo_close</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$finfo</span></span>    )</div>     <p class="para rdfs-comment">    关闭 <span class="function">{@link finfo_open()}</span> 函数所返回的 fileinfo 资源。   </p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.finfo-close-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">finfo</span></dt>       <dd>        <p class="para">        <span class="function">{@link finfo_open()}</span> 函数所返回的 fileinfo 资源。       </p>      </dd>          </dl>    </span>  </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.finfo-close-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.fileinfo.php">Fileinfo 函数</a></div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function finfo_close ($finfo) {}

/**
*<div id="function.finfo-set-flags" class="refentry">  <div class="refnamediv">   <h1 class="refname">finfo_set_flags</h1>   <h1 class="refname">finfo::set_flags</h1>   <p class="verinfo">(PHP &gt;= 5.3.0, PECL fileinfo &gt;= 0.1.0)</p><p class="refpurpose"><span class="refname">finfo_set_flags</span> -- <span class="refname">finfo::set_flags</span> &mdash; <span class="dc-title">设置 libmagic 配置选项</span></p>   </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.finfo-set-flags-description">   <h3 class="title">说明</h3>   <p class="para">过程化风格</p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/finfo.set-flags.php" class="methodname" style="color:#CC7832">finfo_set_flags</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$finfo</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span></span>    )</div>    <p class="para rdfs-comment">面向对象风格</p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span> <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832">{@link finfo::set_flags}</span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span></span>    )</div>     <p class="para rdfs-comment">     此函数用来设置 Fileinfo 选项。     这些选项也可以在调用 <span class="function">{@link finfo_open()}</span> 或者其他 Fileinfo 函数时直接指定。   </p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.finfo-set-flags-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">finfo</span></dt>       <dd>        <p class="para">        <span class="function">{@link finfo_open()}</span> 返回的资源。       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">options</span></dt>       <dd>        <p class="para">        一个 <a href="http://php.net/manual/zh/fileinfo.constants.php" class="link">Fileinfo        常量 </a> 或多个 <a href="http://php.net/manual/zh/fileinfo.constants.php" class="link">Fileinfo 常量 </a> 进行逻辑或运算。       </p>      </dd>          </dl>    </span>  </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.finfo-set-flags-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.fileinfo.php">Fileinfo 函数</a></div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function finfo_set_flags ($finfo, $options) {}

/**
*<div id="function.finfo-file" class="refentry">  <div class="refnamediv">   <h1 class="refname">finfo_file</h1>   <h1 class="refname">finfo::file</h1>   <p class="verinfo">(PHP &gt;= 5.3.0, PECL fileinfo &gt;= 0.1.0)</p><p class="refpurpose"><span class="refname">finfo_file</span> -- <span class="refname">finfo::file</span> &mdash; <span class="dc-title">返回一个文件的信息</span></p>   </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.finfo-file-description">   <h3 class="title">说明</h3>   <p class="para">过程化风格</p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">string</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/finfo.file.php" class="methodname" style="color:#CC7832">finfo_file</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$finfo</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$file_name</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span><span class="initializer"> = FILEINFO_NONE</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$context</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>   ]] )</div>    <p class="para rdfs-comment">面向对象风格</p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span> <span class="type" style="color:#EAB766">string</span> <span class="methodname" style="color:#CC7832">{@link finfo::file}</span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$file_name</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$options</span><span class="initializer"> = FILEINFO_NONE</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$context</span><span class="initializer"> = <strong><span>NULL</span></strong></span></span>   ]] )</div>     <p class="para rdfs-comment">    本函数用来获取一个文件的信息。   </p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.finfo-file-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">finfo</span></dt>       <dd>        <p class="para">        <span class="function">{@link finfo_open()}</span> 函数所返回的 fileinfo 资源。       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">file_name</span></dt>       <dd>        <p class="para">        要检查的文件名。       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">options</span></dt>       <dd>        <p class="para">        一个 <a href="http://php.net/manual/zh/fileinfo.constants.php" class="link">Fileinfo        常量 </a> 或多个 <a href="http://php.net/manual/zh/fileinfo.constants.php" class="link">Fileinfo 常量 </a> 进行逻辑或运算。       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">context</span></dt>       <dd>        <p class="para">        关于 <em>contexts</em> 的更多描述，请参考 <a href="http://php.net/manual/zh/ref.stream.php" class="xref">Stream 函数</a>。       </p>      </dd>          </dl>    </span>  </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.finfo-file-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    返回 <span class="parameter" style="color:#2EACF9">file_name</span> 参数指定的文件信息。    发生错误时返回 <strong><span>FALSE</span></strong> 。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.finfo-file-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-2753">     <p><strong>Example #1 <span class="function"><a href="http://php.net/manual/zh/finfo.file.php" class="function">finfo_file()</a></span> 例程</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$finfo&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">finfo_open</span><span style="color: #007700">(</span><span style="color: #9876AA">FILEINFO_MIME_TYPE</span><span style="color: #007700">);&nbsp;</span><span style="color: #FF8000">//&nbsp;返回&nbsp;mime&nbsp;类型<br /></span><span style="color: #007700">foreach&nbsp;(</span><span style="color: #9876AA">glob</span><span style="color: #007700">(</span><span style="color: #DD0000">"*"</span><span style="color: #007700">)&nbsp;as&nbsp;</span><span style="color: #9876AA">$filename</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #9876AA">finfo_file</span><span style="color: #007700">(</span><span style="color: #9876AA">$finfo</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$filename</span><span style="color: #007700">)&nbsp;.&nbsp;</span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">finfo_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$finfo</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>      <div class="example-contents"><p>以上例程的输出类似于：</p></div>     <div class="example-contents screen" style="background:black;padding-left:5px;"> <div class="cdata"><span> text/html image/gif application/vnd.ms-excel </span></div>     </div>    </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.finfo-file-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function"><a href="http://php.net/manual/zh/finfo.buffer.php" class="function" rel="rdfs-seeAlso">finfo_buffer()</a> - 别名 finfo_buffer()</span></li>    </ul>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.fileinfo.php">Fileinfo 函数</a></div></div>
 * @return mixed a textual description of the contents of the*/
