define ('FILEINFO_MIME_TYPE', 16);

/**
*       返回文件的 mime 编码。     
*/
define ('FILEINFO_MIME_ENCODING', 1024);

/**
*       查看设备的块内容或字符。     
*/
define ('FILEINFO_DEVICES', 8);

/**
*       返回全部匹配的类型。     
*/
define ('FILEINFO_CONTINUE', 32);

/**
