/**
 * <p></p>
 * @link http://php.net/manual/en/ftp.constants.php
 */
define ('FTP_ASCII', 1);

/**
 * <p></p>
 * @link http://php.net/manual/en/ftp.constants.php
 */
define ('FTP_TEXT', 1);

/**
 * <p></p>
 * @link http://php.net/manual/en/ftp.constants.php
 */
define ('FTP_BINARY', 2);

/**
 * <p></p>
 * @link http://php.net/manual/en/ftp.constants.php
 */
define ('FTP_IMAGE', 2);

/**
*      为 GET 和 PUT 请求自动决定恢复和开始的位置      (只能工作在 FTP_AUTOSEEK 打开的情况下)     
*/
define ('FTP_AUTORESUME', -1);

/**
*      更多设置参考函数 <span class="function">{@link ftp_set_option()}</span>。     
*/
define ('FTP_TIMEOUT_SEC', 0);

/**
*      参考函数 <span class="function">{@link ftp_set_option()}</span>。     
*/
define ('FTP_AUTOSEEK', 1);


define('FTP_USEPASVADDRESS', 2);

/**
*      异步传输失败     
*/
define ('FTP_FAILED', 0);

/**
*      异步传输成功     
*/
define ('FTP_FINISHED', 1);

/**
*      异步传输是活动状态的     
*/
define ('FTP_MOREDATA', 2);

