an style="color: #9876AA">imagecreatefromgif</span><span style="color: #007700">(</span><span style="color: #DD0000">'phplogo.gif'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;Get&nbsp;closest&nbsp;colors&nbsp;from&nbsp;the&nbsp;image<br /></span><span style="color: #9876AA">$colors&nbsp;</span><span style="color: #007700">=&nbsp;array();<br /></span><span style="color: #9876AA">$colors</span><span style="color: #007700">[]&nbsp;=&nbsp;</span><span style="color: #9876AA">imagecolorresolvealpha</span><span style="color: #007700">(</span><span style="color: #9876AA">$im</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">255</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">255</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">255</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">0</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$colors</span><span style="color: #007700">[]&nbsp;=&nbsp;</span><span style="color: #9876AA">imagecolorresolvealpha</span><span style="color: #007700">(</span><span style="color: #9876AA">$im</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">0</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">0</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">200</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">127</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;Output<br /></span><span style="color: #9876AA">print_r</span><span style="color: #007700">(</span><span style="color: #9876AA">$colors</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">imagedestroy</span><span style="color: #007700">(</span><span style="color: #9876AA">$im</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>      </div>     <div class="example-contents"><p>以上例程的输出类似于：</p></div>    <div class="example-contents screen" style="background:black;padding-left:5px;"> <div class="cdata"><span> Array (     [0] =&gt; 89     [1] =&gt; 85 ) </span></div>    </div>   </div>  </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.imagecolorresolvealpha-notes">   <h3 class="title">注释</h3>   <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>: <span class="simpara">此函数需要 GD 2.0.1 或更高版本(推荐 2.0.28 及更高版本)。</span></p></blockquote>  </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.imagecolorresolvealpha-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link imagecolorclosestalpha()} - 取得与指定的颜色加透明度最接近的颜色</span></li>    </ul>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.image.php">GD 和图像处理 函数</a></div></div>
