<span style="color: #9876AA">$im&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">imagecreatefrompng</span><span style="color: #007700">(</span><span style="color: #DD0000">'figures/imagecolorclosest.png'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">imagetruecolortopalette</span><span style="color: #007700">(</span><span style="color: #9876AA">$im</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">false</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">255</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;Search&nbsp;colors&nbsp;(RGB)<br /></span><span style="color: #9876AA">$colors&nbsp;</span><span style="color: #007700">=&nbsp;array(<br />&nbsp;&nbsp;&nbsp;&nbsp;array(</span><span style="color: #9876AA">254</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">145</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">154</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">50</span><span style="color: #007700">),<br />&nbsp;&nbsp;&nbsp;&nbsp;array(</span><span style="color: #9876AA">153</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">145</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">188</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">127</span><span style="color: #007700">),<br />&nbsp;&nbsp;&nbsp;&nbsp;array(</span><span style="color: #9876AA">153</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">90</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">145</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">0</span><span style="color: #007700">),<br />&nbsp;&nbsp;&nbsp;&nbsp;array(</span><span style="color: #9876AA">255</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">137</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">92</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">84</span><span style="color: #007700">)<br />);<br /><br /></span><span style="color: #FF8000">//&nbsp;Loop&nbsp;through&nbsp;each&nbsp;search&nbsp;and&nbsp;find&nbsp;the&nbsp;closest&nbsp;color&nbsp;in&nbsp;the&nbsp;palette.<br />//&nbsp;Return&nbsp;the&nbsp;search&nbsp;number,&nbsp;the&nbsp;search&nbsp;RGB&nbsp;and&nbsp;the&nbsp;converted&nbsp;RGB&nbsp;match<br /></span><span style="color: #007700">foreach(</span><span style="color: #9876AA">$colors&nbsp;</span><span style="color: #007700">as&nbsp;</span><span style="color: #9876AA">$id&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">$rgb</span><span style="color: #007700">)<br />{<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$result&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">imagecolorclosestalpha</span><span style="color: #007700">(</span><span style="color: #9876AA">$im</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$rgb</span><span style="color: #007700">[</span><span style="color: #9876AA">0</span><span style="color: #007700">],&nbsp;</span><span style="color: #9876AA">$rgb</span><span style="color: #007700">[</span><span style="color: #9876AA">1</span><span style="color: #007700">],&nbsp;</span><span style="color: #9876AA">$rgb</span><span style="color: #007700">[</span><span style="color: #9876AA">2</span><span style="color: #007700">],&nbsp;</span><span style="color: #9876AA">$rgb</span><span style="color: #007700">[</span><span style="color: #9876AA">3</span><span style="color: #007700">]);<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$result&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">imagecolorsforindex</span><span st