n><span style="color: #9876AA">$x1</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$y1</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$x2</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$y2</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$color</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$t&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">$thick&nbsp;</span><span style="color: #007700">/&nbsp;</span><span style="color: #9876AA">2&nbsp;</span><span style="color: #007700">-&nbsp;</span><span style="color: #9876AA">0.5</span><span style="color: #007700">;<br />&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(</span><span style="color: #9876AA">$x1&nbsp;</span><span style="color: #007700">==&nbsp;</span><span style="color: #9876AA">$x2&nbsp;</span><span style="color: #007700">||&nbsp;</span><span style="color: #9876AA">$y1&nbsp;</span><span style="color: #007700">==&nbsp;</span><span style="color: #9876AA">$y2</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;</span><span style="color: #9876AA">imagefilledrectangle</span><span style="color: #007700">(</span><span style="color: #9876AA">$image</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">min</span><span style="color: #007700">(</span><span style="color: #9876AA">$x1</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$x2</span><span style="color: #007700">)&nbsp;-&nbsp;</span><span style="color: #9876AA">$t</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">min</span><span style="color: #007700">(</span><span style="color: #9876AA">$y1</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$y2</span><span style="color: #007700">)&nbsp;-&nbsp;</span><span style="color: #9876AA">$t</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">max</span><span style="color: #007700">(</span><span style="color: #9876AA">$x1</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$x2</span><span style="color: #007700">)&nbsp;+&nbsp;</span><span style="color: #9876AA">$t</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">max</span><span style="color: #007700">(</span><span style="color: #9876AA">$y1</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$y2</span><span style="color: #007700">)&nbsp;+&nbsp;</span><span style="color: #9876AA">$t</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">$color</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$k&nbsp;</span><span style="color: #007700">=&nbsp;(</span><span style="color: #9876AA">$y2&nbsp;</span><span style="color: #007700">-&nbsp;</span><span style="color: #9876AA">$y1</span><span style="color: #007700">)&nbsp;/&nbsp;(</span><span style="color: #9876AA">$x2&nbsp;</span><span style="color: #007700">-&nbsp;</span><span style="color: #9876AA">$x1</span><span style="color: #007700">);&nbsp;</span><span style="color: #FF8000">//y&nbsp;=&nbsp;kx&nbsp;+&nbsp;q<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$a&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">$t&nbsp;</span><span style="color: #007700">/&nbsp;</span><span style="color: #9876AA">sqrt</span><span style="color: #007700">(</span><span style="color: #9876AA">1&nbsp;</span><span style="color: #007700">+&nbsp;</span><span style="color: #9876AA">pow</span><span style="color: #007700">(</span><span style="color: #9876AA">$k</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">2</span><span style="color: #007700">));<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$points&nbsp;</span><span style="color: #007700">=&nbsp;array(<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">$x1&nbsp;</span><span style="color: #007700">-&nbsp;(</span><span style="color: #9876AA">1</span><span style="color: #007700">+</span><span style="color: #9876AA">$k</span><span style="color: #007700">)*</span><span style="color: #9876AA">$a</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">$y1&nbsp;</span><span style="color: #007700">+&nbsp;(</span><span style="color: #9876AA">1</span><span style="color: #007700">-</span><span style="color: #9876AA">$k</span><span style="color: #007700">)*</span><span style="color: #9876AA">$a</span><span style="color: #007700">),<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">$x1&nbsp;</span><span style="color: #007700">-&nbsp;(</span><span style="color: #9876AA">1</span><span style="color: #007700">-</span><span style="color: #9876AA">$k</span><span style="color: #007700">)*</span><span style="color: #9876AA">$a</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">$y1&nbsp;</span><span style="color: #007700">-&nbsp;(</span><span style="color: #9876AA">1</span><span style="color: #007700">+</span><span style="color: #9876AA">$k</span><span style="color: #007700">)*</span><span style="color: #9876AA">$a</span><span style="color: #007700">),<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">$x2&nbsp;</span><span style="color: #007700">+&nbsp;(</span><span style="color: #9876AA">1</span><span style="color: #007700">+</span><span style="color: #9876AA">$k</span><span style="color: #007700">)*</span><span style="color: #9876AA">$a</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">$y2&nbsp;</span><span style="color: #007700">-&nbsp;(</span><span style="color: #9876AA">1</span><span style="color: #007700">-</span><span style="color: #9876AA">$k</span><span style="color: #007700">)*</span><span style="color: #9876AA">$a</span><span style="color: #007700">),<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">$x2&nbsp;</span><span style="color: #007700">+&nbsp;(</span><span style="color: #9876AA">1</span><span style="color: #007700">-</span><span style="color: #9876AA">$k</span><span style="color: #007700">)*</span><span style="color: #9876AA">$a</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">round</span><span style="color: #007700">(</span><span style="color: #9876AA">$y2&nbsp;</span><span style="color: #007700">+&nbsp;(</span><span style="color: #9876AA">1</span><span style="color: #007700">+</span><span style="color: #9876AA">$k</span><span style="color: #007700">)*</span><span style="color: #9876AA">$a</span><span style="color: #007700">),<br />&nbsp;&nbsp;&nbsp;&nbsp;);<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">imagefilledpolygon</span><span style="color: #007700">(</span><span style="color: #9876AA">$image</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$points</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">4</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$color</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;</span><span style="color: #9876AA">imagepolygon</span><span style="color: #007700">(</span><span style="color: #9876AA">$image</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$points</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">4</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$color</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>    </div>    </div>   <p class="para">    参见 <span class="function">{@link imagecreatetruecolor()}</span> 和    <span class="function">{@link imagecolorallocate()}</span>。   </p>  </div>  <div class="up"><a href="http://php.net/manual/zh/ref.image.php">GD 和图像处理 函数</a></div></div>
