sp;with&nbsp;valid&nbsp;key-value&nbsp;pairs&nbsp;<br /></span><span style="color: #9876AA">$options&nbsp;</span><span style="color: #007700">=&nbsp;array(</span><span style="color: #DD0000">'autocommit'&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">DB2_AUTOCOMMIT_OFF</span><span style="color: #007700">,&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #DD0000">'binmode'&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">DB2_PASSTHRU</span><span style="color: #007700">,<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #DD0000">'db2_attr_case'&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">DB2_CASE_UPPER</span><span style="color: #007700">,<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #DD0000">'cursor'&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">DB2_SCROLLABLE</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stmt&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'SELECT&nbsp;*&nbsp;FROM&nbsp;EMPLOYEE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;Call&nbsp;the&nbsp;function&nbsp;using&nbsp;the&nbsp;wrong&nbsp;resource,&nbsp;and&nbsp;the&nbsp;correct&nbsp;options&nbsp;array,&nbsp;and&nbsp;type&nbsp;values&nbsp;<br /></span><span style="color: #9876AA">$result&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_set_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$options</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">1</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;Check&nbsp;if&nbsp;all&nbsp;options&nbsp;could&nbsp;be&nbsp;set&nbsp;correctly&nbsp;<br /></span><span style="color: #007700">if(</span><span style="color: #9876AA">$result</span><span style="color: #007700">)<br />{<br />&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">'Options&nbsp;Set&nbsp;Successfully'</span><span style="color: #007700">;<br />}<br />else<br />{<br />&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">'Could&nbsp;Not&nbsp;Set&nbsp;Options'</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>         </div>          <div class="example-contents"><p>以上例程会输出：</p></div>         <div class="example-contents screen" style="background:black;padding-left:5px;"><div class="cdata"><span>Could Not Set Options</span></div></div>       </div>        <div class="example" id="db2-pclose.example.basic">         <p><strong>Example #7 Putting it all together</strong></p>         <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">//&nbsp;Database&nbsp;Connection&nbsp;Parameters&nbsp;<br /></span><span style="color: #9876AA">$database&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'SAMPLE'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$hostname&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'localhost'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$port&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">50000</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$protocol&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'TCPIP'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$username&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'db2inst1'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$password&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'ibmdb2'</span><span style="color: #007700">;<br /><br /></span><span style="color: #FF8000">//&nbsp;Connection&nbsp;String&nbsp;<br /></span><span style="color: #9876AA">$conn_string&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">"DRIVER={IBM&nbsp;DB2&nbsp;ODBC&nbsp;DRIVER};DATABASE=</span><span style="color: #9876AA">$database</span><span style="color: #DD0000">;"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$conn_string&nbsp;</span><span style="color: #007700">.=&nbsp;</span><span style="color: #DD0000">"HOSTNAME=</span><span style="color: #9876AA">$hostname</span><span style="color: #DD0000">;PORT=</span><span style="color: #9876AA">$port</span><span style="color: #DD0000">;PROTOCOL=</span><span style="color: #9876AA">$protocol</span><span style="color: #DD0000">;"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$conn_string&nbsp;</span><span style="color: #007700">.=&nbsp;</span><span style="color: #DD0000">"UID=</span><span style="color: #9876AA">$username</span><span style="color: #DD0000">;PWD=</span><span style="color: #9876AA">$password</span><span style="color: #DD0000">;"</span><span style="color: #007700">;<br /><br /></span><span style="color: #FF8000">//&nbsp;Obtain&nbsp;Connection&nbsp;Resource&nbsp;<br /></span><span style="color: #9876AA">$conn&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn_string</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">''</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">''</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;Create&nbsp;the&nbsp;associative&nbsp;options&nbsp;array&nbsp;with&nbsp;valid&nbsp;key-value&nbsp;pairs&nbsp;<br /></span><span style="color: #9876AA">$options&nbsp;</span><span style="color: #007700">=&nbsp;array(</span><span style="color: #DD0000">'db2_attr_case'&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">DB2_CASE_LOWER</span><span style="color: #007700">,<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #DD0000">'cursor'&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">DB2_SCROLLABLE</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stmt&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'SELECT&nbsp;*&nbsp;FROM&nbsp;EMPLOYEE&nbsp;WHERE&nbsp;EMPNO&nbsp;=&nbsp;?&nbsp;OR&nbsp;EMPNO&nbsp;=&nbsp;?'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">//&nbsp;Call&nbsp;the&nbsp;function&nbsp;using&nbsp;the&nbsp;correct&nbsp;resource,&nbsp;options&nbsp;array,&nbsp;and&nbsp;type&nbsp;values&nbsp;<br /></span><span style="color: #9876AA">$option_result&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_set_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$options</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">2</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$result&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">,&nbsp;array(</span><span style="color: #DD0000">'000130'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'000140'</span><span style="color: #007700">));<br /><br /></span><span style="color: #FF8000">//&nbsp;Get&nbsp;Row&nbsp;2&nbsp;before&nbsp;Row&nbsp;1&nbsp;since&nbsp;Scrollable&nbsp;Cursor&nbsp;<br /></span><span style="color: #9876AA">print_r</span><span style="color: #007700">(</span><span style="color: #9876AA">db2_fetch_assoc</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">2</span><span style="color: #007700">));<br />print&nbsp;</span><span style="color: #DD0000">'&lt;br&nbsp;/&gt;&lt;br&nbsp;/&gt;'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">print_r</span><span style="color: #007700">(</span><span style="color: #9876AA">db2_fetch_assoc</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">1</span><span style="color: #007700">));<br /><br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>         </div>          <div class="example-contents"><p>以上例程会输出：</p></div>         <div class="example-contents screen" style="background:black;padding-left:5px;"><div class="cdata"><span>Array (     [empno] =&gt; 000140     [firstnme] =&gt; HEATHER     [midinit] =&gt; A     [lastname] =&gt; NICHOLLS     [workdept] =&gt; C01     [phoneno] =&gt; 1793     [hiredate] =&gt; 1976-12-15     [job] =&gt; ANALYST     [edlevel] =&gt; 18     [sex] =&gt; F     [birthdate] =&gt; 1946-01-19     [salary] =&gt; 28420.00     [bonus] =&gt; 600.00     [comm] =&gt; 2274.00 )  Array (     [empno] =&gt; 000130     [firstnme] =&gt; DELORES     [midinit] =&gt; M     [lastname] =&gt; QUINTANA     [workdept] =&gt; C01     [phoneno] =&gt; 4578     [hiredate] =&gt; 1971-07-28     [job] =&gt; ANALYST     [edlevel] =&gt; 16     [sex] =&gt; F     [birthdate] =&gt; 1925-09-15     [salary] =&gt; 23800.00     [bonus] =&gt; 500.00     [comm] =&gt; 1904.00 )</span></div></div>       </div>       <div class="example" id="example-1277">         <p><strong>Example #8 i5/OS cursors are read-only</strong></p>         <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />&nbsp;&nbsp;$conn&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">""</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">""</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">""</span><span style="color: #007700">,&nbsp;array(</span><span style="color: #DD0000">"i5_lib"</span><span style="color: #007700">=&gt;</span><span style="color: #DD0000">"nobody"</span><span style="color: #007700">));<br />&nbsp;&nbsp;</span><span style="color: #9876AA">$stmt&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'select&nbsp;*&nbsp;from&nbsp;names&nbsp;where&nbsp;first&nbsp;=&nbsp;?'</span><span style="color: #007700">);<br />&nbsp;&nbsp;</span><span style="color: #9876AA">$name&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">"first2"</span><span style="color: #007700">;<br />&nbsp;&nbsp;</span><span style="color: #9876AA">db2_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">1</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"name"</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">DB2_PARAM_IN</span><span style="color: #007700">);<br />&nbsp;&nbsp;</span><span style="color: #9876AA">$options&nbsp;</span><span style="color: #007700">=&nbsp;array(</span><span style="color: #DD0000">"i5_fetch_only"</span><span style="color: #007700">=&gt;</span><span style="color: #9876AA">DB2_I5_FETCH_ON</span><span style="color: #007700">);<br />&nbsp;&nbsp;</span><span style="color: #9876AA">db2_set_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">,</span><span style="color: #9876AA">$options</span><span style="color: #007700">,</span><span style="color: #9876AA">0</span><span style="color: #007700">);<br />&nbsp;&nbsp;if&nbsp;(</span><span style="color: #9876AA">db2_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">))&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;(</span><span style="color: #9876AA">$row&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">db2_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">))&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"</span><span style="color: #007700">{</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">0</span><span style="color: #007700">]}</span><span style="color: #DD0000">&nbsp;</span><span style="color: #007700">{</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">1</span><span style="color: #007700">]}</span><span style="color: #DD0000">"</span><span style="color: #007700">;<br />&nbsp;&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;}<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>         </div>          <div class="example-contents"><p>以上例程会输出：</p></div>         <div class="example-contents screen" style="background:black;padding-left:5px;"> <div class="cdata"><span> first2 last2 </span></div>         </div>       </div>     </span>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.db2-set-option-seealso">     <h3 class="title">参见</h3>     <span>       <ul class="simplelist">         <li class="member"><span class="function">{@link db2_connect()} - Returns a connection to a database</span></li>         <li class="member"><span class="function">{@link db2_pconnect()} - Returns a persistent connection to a database</span></li>         <li class="member"><span class="function">{@link db2_exec()} - Executes an SQL statement directly</span></li>         <li class="member"><span class="function">{@link db2_prepare()} - Prepares an SQL statement to be executed</span></li>         <li class="member"><span class="function">{@link db2_cursor_type()} - Returns the cursor type used by a statement resource</span></li>       </ul>     </span>   </div>  <div class="up"><a href="http://php.net/manual/zh/ref.ibm-db2.php">IBM DB2 函数</a></div></div>
 * @return bool true on success or false on failure.*/
