function ibase_fetch_assoc ($result, $fetch_flag = null) {}

/**
*<div id="function.ibase-fetch-object" class="refentry">  <div class="refnamediv">   <h1 class="refname">ibase_fetch_object</h1>   <p class="verinfo">(PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">ibase_fetch_object</span> &mdash; <span class="dc-title">Get an object from a InterBase database</span></p>   </div>    <div class="refsect1 description" id="refsect1-function.ibase-fetch-object-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description">    <span class="type">object</span> <span class="methodname"><strong>ibase_fetch_object</strong></span>     ( <span class="methodparam"><span class="type">resource</span> <code class="parameter">$result_id</code></span>    [, <span class="methodparam"><span class="type">int</span> <code class="parameter">$fetch_flag</code><span class="initializer"> = 0</span></span>   ] )</div>    <p class="para rdfs-comment">    Fetches a row as a pseudo-object from a given result identifier.   </p>   <p class="para">    Subsequent calls to <span class="function"><strong>ibase_fetch_object()</strong></span>    return the next row in the result set.   </p>  </div>    <div class="refsect1 parameters" id="refsect1-function.ibase-fetch-object-parameters">   <h3 class="title">参数</h3>   <p class="para">    <dl>            <dt> <code class="parameter">result_id</code></dt>       <dd>        <p class="para">        An InterBase result identifier obtained either by        <span class="function">{@link ibase_query()}</span> or <span class="function">{@link ibase_execute()}</span>.       </p>      </dd>                 <dt> <code class="parameter">fetch_flag</code></dt>       <dd>        <p class="para">        <code class="parameter">fetch_flag</code> is a combination of the constants        <strong><code>IBASE_TEXT</code></strong> and <strong><code>IBASE_UNIXTIME</code></strong>        ORed together. Passing <strong><code>IBASE_TEXT</code></strong> will cause this        function to return BLOB contents instead of BLOB ids. Passing        <strong><code>IBASE_UNIXTIME</code></strong> will cause this function to return        date/time values as Unix timestamps instead of as formatted strings.       </p>      </dd>          </dl>    </p>  </div>    <div class="refsect1 returnvalues" id="refsect1-function.ibase-fetch-object-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns an object with the next row information, or <strong><code>FALSE</code></strong> if there are    no more rows.   </p>  </div>    <div class="refsect1 examples" id="refsect1-function.ibase-fetch-object-examples">   <h3 class="title">范例</h3>   <p class="para">    <div class="example" id="example-1194">     <p><strong>Example #1 <span class="function"><strong>ibase_fetch_object()</strong></span> example</strong></p>     <div class="example-contents"> <div class="phpcode"><code><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$dbh&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ibase_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$host</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$username</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'SELECT&nbsp;*&nbsp;FROM&nbsp;tblname'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$sth&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ibase_query</span><span style="color: #007700">(</span><span style="color: #9876AA">$dbh</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br />while&nbsp;(</span><span style="color: #9876AA">$row&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ibase_fetch_object</span><span style="color: #007700">(</span><span style="color: #9876AA">$sth</span><span style="color: #007700">))&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #9876AA">$row</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">email&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">ibase_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$dbh</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </code></div>     </div>     </div>      </p>  </div>    <div class="refsect1 seealso" id="refsect1-function.ibase-fetch-object-seealso">   <h3 class="title">参见</h3>   <p class="para">    <ul class="simplelist">     <li class="member"><span class="function">{@link ibase_fetch_row()} - Fetch a row from an InterBase database</span></li>     <li class="member"><span class="function">{@link ibase_fetch_assoc()} - Fetch a result row from a query as an associative array</span></li>    </ul>   </p>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.ibase.php">Firebird/InterBase 函数</a></div></div>
 * @return object an object with the next row information, or false if there are*/
function ibase_fetch_object ($result_id, $fetch_flag = null) {}

