function ibase_rollback ($link_or_trans_identifier = null) {}

/**
*<div id="function.ibase-commit-ret" class="refentry">  <div class="refnamediv">   <h1 class="refname">ibase_commit_ret</h1>   <p class="verinfo">(PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">ibase_commit_ret</span> &mdash; <span class="dc-title">Commit a transaction without closing it</span></p>   </div>    <div class="refsect1 description" id="refsect1-function.ibase-commit-ret-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description">    <span class="type">bool</span> <span class="methodname"><strong>ibase_commit_ret</strong></span>     ([ <span class="methodparam"><span class="type">resource</span> <code class="parameter">$link_or_trans_identifier</code><span class="initializer"> = <strong><code>NULL</code></strong></span></span>   ] )</div>    <p class="para rdfs-comment">    Commits a transaction without closing it.   </p>  </div>    <div class="refsect1 parameters" id="refsect1-function.ibase-commit-ret-parameters">   <h3 class="title">参数</h3>   <p class="para">    <dl>            <dt> <code class="parameter">link_or_trans_identifier</code></dt>       <dd>        <p class="para">        If called without an argument, this function commits the default        transaction of the default link. If the argument is a connection        identifier, the default transaction of the corresponding connection        will be committed. If the argument is a transaction identifier, the        corresponding transaction will be committed. The transaction context        will be retained, so statements executed from within this transaction        will not be invalidated.       </p>      </dd>          </dl>    </p>  </div>    <div class="refsect1 returnvalues" id="refsect1-function.ibase-commit-ret-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><code>TRUE</code></strong>， 或者在失败时返回 <strong><code>FALSE</code></strong>。   </p>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.ibase.php">Firebird/InterBase 函数</a></div></div>
 * @return bool true on success or false on failure.*/
function ibase_commit_ret ($link_or_trans_identifier = null) {}

