/**
*<div id="class.collator" class="reference">  <h1 class="title">The Collator class</h1>     <div class="partintro"><p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7, PECL intl &gt;= 1.0.0)</p>       <div class="section" id="collator.intro">    <h2 class="title">简介</h2>    <p class="para">     Provides string comparison capability with support for appropriate     locale-sensitive sort orderings.    </p>   </div>       <div class="section" id="collator.synopsis">    <h2 class="title">类摘要</h2>         <div class="classsynopsis">     <div class="ooclass">           </div>           <div class="classsynopsisinfo">      <span class="ooclass">       <strong class="classname">Collator</strong>      </span>      {</div>                 <div class="classsynopsisinfo classsynopsisinfo_comment">// 方法 </div>     <div class="constructorsynopsis dc-description">    <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.construct.php" class="methodname" style="color:#CC7832">__construct</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$locale</span></span>    )</div>      <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.asort.php" class="methodname" style="color:#CC7832">asort</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#2EACF9">&$arr</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$sort_flag</span></span>   ] )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">int</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.compare.php" class="methodname" style="color:#CC7832">compare</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$str1</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$str2</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="modifier">static</span>    <span class="type" style="color:#EAB766">Collator</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.create.php" class="methodname" style="color:#CC7832">create</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$locale</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">int</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.getattribute.php" class="methodname" style="color:#CC7832">getAttribute</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$attr</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">int</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.geterrorcode.php" class="methodname" style="color:#CC7832">getErrorCode</a></span>     ( <span class="methodparam">void</span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">string</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.geterrormessage.php" class="methodname" style="color:#CC7832">getErrorMessage</a></span>     ( <span class="methodparam">void</span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">string</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.getlocale.php" class="methodname" style="color:#CC7832">getLocale</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$type</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">string</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.getsortkey.php" class="methodname" style="color:#CC7832">getSortKey</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$str</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">int</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.getstrength.php" class="methodname" style="color:#CC7832">getStrength</a></span>     ( <span class="methodparam">void</span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.setattribute.php" class="methodname" style="color:#CC7832">setAttribute</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$attr</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$val</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.setstrength.php" class="methodname" style="color:#CC7832">setStrength</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$strength</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.sortwithsortkeys.php" class="methodname" style="color:#CC7832">sortWithSortKeys</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#2EACF9">&$arr</span></span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><a href="http://php.net/manual/zh/collator.sort.php" class="methodname" style="color:#CC7832">sort</a></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#2EACF9">&$arr</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$sort_flag</span></span>   ] )</div>     }</div>        </div>          <div class="section" id="intl.collator-constants">  <h2 class="title">预定义常量</h2>  <p class="para">   <dl>          <dt id="collator.constants.french-collation">      <strong><span>Collator::FRENCH_COLLATION</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <p class="para">       Sort strings with different accents from the back of the string. This       attribute is automatically set to       <em class="emphasis">On</em>       for the French locales and a few others. Users normally would not need       to explicitly set this attribute. There is a string comparison       performance cost when it is set <em class="emphasis">On</em>,       but sort key length is unaffected. Possible values are:       <ul class="simplelist">        <li class="member"><strong><span>Collator::ON</span></strong></li>        <li class="member"><strong><span>Collator::OFF</span></strong>(default)</li>        <li class="member"><strong><span>Collator::DEFAULT_VALUE</span></strong></li>       </ul>      </p>      <p class="para">       <div class="example" id="example-2894">        <p><strong>Example #1 FRENCH_COLLATION rules</strong></p>        <div class="example-contents"><p>         <ul class="simplelist">          <li class="member">F=OFF cote &lt; coté &lt; côte &lt; côté </li>          <li class="member">F=ON cote &lt; côte &lt; coté &lt; côté</li>         </ul>        </p></div>       </div>      </p>     </dd>               <dt id="collator.constants.alternate-handling">      <strong><span>Collator::ALTERNATE_HANDLING</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <p class="para">       The Alternate attribute is used to control the handling of the so called       variable characters in the UCA: whitespace, punctuation and symbols. If       Alternate is set to <em class="emphasis">NonIgnorable</em>       (N), then differences among these characters are of the same importance       as differences among letters. If Alternate is set to       <em class="emphasis">Shifted</em>       (S), then these characters are of only minor importance. The       <em class="emphasis">Shifted</em> value is often used in combination with       <em class="emphasis">Strength</em>       set to Quaternary. In such a case, whitespace, punctuation, and symbols       are considered when comparing strings, but only if all other aspects of       the strings (base letters, accents, and case) are identical. If       Alternate is not set to Shifted, then there is no difference between a       Strength of 3 and a Strength of 4. For more information and examples,       see Variable_Weighting in the       <a href="http://www.unicode.org/reports/tr10/" class="link external">&raquo;&nbsp;UCA</a>.       The reason the Alternate values are not simply       <em class="emphasis">On</em> and <em class="emphasis">Off</em>       is that additional Alternate values may be added in the future. The UCA       option Blanked is expressed with Strength set to 3, and Alternate set to       Shifted. The default for most locales is NonIgnorable. If Shifted is       selected, it may be slower if there are many strings that are the same       except for punctuation; sort key length will not be affected unless the       strength level is also increased.      </p>      <p class="para">       Possible values are:       <ul class="simplelist">        <li class="member"><strong><span>Collator::NON_IGNORABLE</span></strong>(default)</li>        <li class="member"><strong><span>Collator::SHIFTED</span></strong></li>        <li class="member"><strong><span>Collator::DEFAULT_VALUE</span></strong></li>       </ul>      </p>      <p class="para">       <div class="example" id="example-2895">        <p><strong>Example #2 ALTERNATE_HANDLING rules</strong></p>        <div class="example-contents"><p>         <ul class="simplelist">          <li class="member">          S=3, A=N di Silva &lt; Di Silva &lt; diSilva &lt; U.S.A. &lt; USA          </li>           <li class="member">          S=3, A=S di Silva = diSilva &lt; Di Silva &lt; U.S.A. = USA          </li>           <li class="member">          S=4, A=S di Silva &lt; diSilva &lt; Di Silva &lt; U.S.A. &lt; USA          </li>         </ul>        </p></div>       </div>      </p>     </dd>               <dt id="collator.constants.case-first">      <strong><span>Collator::CASE_FIRST</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <p class="para">       The Case_First attribute is used to control whether uppercase letters       come before lowercase letters or vice versa, in the absence of other       differences in the strings. The possible values are       <em class="emphasis">Uppercase_First</em>       (U) and <em class="emphasis">Lowercase_First</em>       (L), plus the standard <em class="emphasis">Default</em>       and <em class="emphasis">Off</em>.       There is almost no difference between the Off and Lowercase_First       options in terms of results, so typically users will not use       Lowercase_First: only Off or Uppercase_First. (People interested in the       detailed differences between X and L should consult the <em>Collation       Customization</em>). Specifying either L or U won&#039;t affect string comparison       performance, but will affect the sort key length.      </p>      <p class="para">       Possible values are:       <ul class="simplelist">        <li class="member"><strong><span>Collator::OFF</span></strong>(default)</li>        <li class="member"><strong><span>Collator::LOWER_FIRST</span></strong></li>        <li class="member"><strong><span>Collator::UPPER_FIRST</span></strong></li>        <li class="member"><strong><span>Collator:DEFAULT</span></strong></li>       </ul>      </p>      <p class="para">       <div class="example" id="example-2896">        <p><strong>Example #3 CASE_FIRST rules</strong></p>        <div class="example-contents"><p>         <ul class="simplelist">          <li class="member">C=X or C=L &quot;china&quot; &lt; &quot;China&quot; &lt; &quot;denmark&quot; &lt; &quot;Denmark&quot;</li>           <li class="member">C=U &quot;China&quot; &lt; &quot;china&quot; &lt; &quot;Denmark&quot; &lt; &quot;denmark&quot;</li>         </ul>        </p></div>       </div>      </p>     </dd>               <dt id="collator.constants.case-level">      <strong><span>Collator::CASE_LEVEL</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <p class="para">       The Case_Level attribute is used when ignoring accents but not case. In       such a situation, set Strength to be <em class="emphasis">Primary</em>,       and Case_Level to be <em class="emphasis">On</em>.       In most locales, this setting is Off by default. There is a small       string comparison performance and sort key impact if this attribute is       set to be <em class="emphasis">On</em>.      </p>      <p class="para">       Possible values are:       <ul class="simplelist">        <li class="member"><strong><span>Collator::OFF</span></strong>(default)</li>        <li class="member"><strong><span>Collator::ON</span></strong></li>        <li class="member"><strong><span>Collator::DEFAULT_VALUE</span></strong></li>       </ul>      </p>      <p class="para">       <div class="example" id="example-2897">        <p><strong>Example #4 CASE_LEVEL rules</strong></p>        <div class="example-contents"><p>         <ul class="simplelist">          <li class="member">S=1, E=X role = Role = rôle</li>           <li class="member">S=1, E=O role = rôle &lt; Role</li>         </ul>        </p></div>       </div>      </p>     </dd>               <dt id="collator.constants.normalization-mode">      <strong><span>Collator::NORMALIZATION_MODE</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <p class="para">       The Normalization setting determines whether text is thoroughly       normalized or not in comparison. Even if the setting is off (which is       the default for many locales), text as represented in common usage will       compare correctly (for details, see UTN #5). Only if the accent marks       are in noncanonical order will there be a problem. If the setting is       <em class="emphasis">On</em>,       then the best results are guaranteed for all possible text input.       There is a medium string comparison performance cost if this attribute       is <em class="emphasis">On</em>,       depending on the frequency of sequences that require normalization.       There is no significant effect on sort key length. If the input text is       known to be in NFD or NFKD normalization forms, there is no need to       enable this Normalization option.      </p>      <p class="para">       Possible values are:       <ul class="simplelist">        <li class="member"><strong><span>Collator::OFF</span></strong>(default)</li>        <li class="member"><strong><span>Collator::ON</span></strong></li>        <li class="member"><strong><span>Collator::DEFAULT_VALUE</span></strong></li>       </ul>      </p>     </dd>               <dt id="collator.constants.strength">      <strong><span>Collator::STRENGTH</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <p class="para">       The ICU Collation Service supports many levels of comparison (named       &quot;Levels&quot;, but also known as &quot;Strengths&quot;). Having these categories       enables ICU to sort strings precisely according to local conventions.       However, by allowing the levels to be selectively employed, searching       for a string in text can be performed with various matching conditions.       For more detailed information, see       <span class="function"><a href="http://php.net/manual/zh/collator.setstrength.php" class="function">collator_set_strength()</a></span> chapter.      </p>      <p class="para">       Possible values are:       <ul class="simplelist">        <li class="member"><strong><span>Collator::PRIMARY</span></strong></li>        <li class="member"><strong><span>Collator::SECONDARY</span></strong></li>        <li class="member"><strong><span>Collator::TERTIARY</span></strong>(default)</li>        <li class="member"><strong><span>Collator::QUATERNARY</span></strong></li>        <li class="member"><strong><span>Collator::IDENTICAL</span></strong></li>        <li class="member"><strong><span>Collator::DEFAULT_VALUE</span></strong></li>       </ul>      </p>     </dd>               <dt id="collator.constants.hiragana-quaternary-mode">      <strong><span>Collator::HIRAGANA_QUATERNARY_MODE</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <p class="para">       Compatibility with JIS x 4061 requires the introduction of an additional       level to distinguish Hiragana and Katakana characters. If compatibility       with that standard is required, then this attribute should be set       <em class="emphasis">On</em>,       and the strength set to Quaternary. This will affect sort key length       and string comparison string comparison performance.      </p>      <p class="para">       Possible values are:       <ul class="simplelist">        <li class="member"><strong><span>Collator::OFF</span></strong>(default)</li>        <li class="member"><strong><span>Collator::ON</span></strong></li>        <li class="member"><strong><span>Collator::DEFAULT_VALUE</span></strong></li>       </ul>      </p>     </dd>               <dt id="collator.constants.numeric-collation">      <strong><span>Collator::NUMERIC_COLLATION</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <p class="para">       When turned on, this attribute generates a collation key for the numeric       value of substrings of digits. This is a way to get &#039;100&#039; to sort AFTER       &#039;2&#039;.      </p>      <p class="para">       Possible values are:       <ul class="simplelist">        <li class="member"><strong><span>Collator::OFF</span></strong>(default)</li>        <li class="member"><strong><span>Collator::ON</span></strong></li>        <li class="member"><strong><span>Collator::DEFAULT_VALUE</span></strong></li>       </ul>      </p>     </dd>               <dt id="collator.constants.default-value">      <strong><span>Collator::DEFAULT_VALUE</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.primary">      <strong><span>Collator::PRIMARY</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.secondary">      <strong><span>Collator::SECONDARY</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.tertiary">      <strong><span>Collator::TERTIARY</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.default-strength">      <strong><span>Collator::DEFAULT_STRENGTH</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.quaternary">      <strong><span>Collator::QUATERNARY</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.identical">      <strong><span>Collator::IDENTICAL</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.off">      <strong><span>Collator::OFF</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.on">      <strong><span>Collator::ON</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.shifted">      <strong><span>Collator::SHIFTED</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.non-ignorable">      <strong><span>Collator::NON_IGNORABLE</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.lower-first">      <strong><span>Collator::LOWER_FIRST</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>               <dt id="collator.constants.upper-first">      <strong><span>Collator::UPPER_FIRST</span></strong>      (<span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.integer.php" class="type integer" style="color:#EAB766">integer</a></span>)     </dt>      <dd>       <span class="simpara">       </span>     </dd>        </dl>   </p> </div>   </div>                                                                                         <h2>Table of Contents</h2><ul class="chunklist chunklist_reference"><li>{@link Collator::asort} — Sort array maintaining index association</li><li>{@link Collator::compare} — Compare two Unicode strings</li><li>{@link Collator::__construct} — Create a collator</li><li>{@link Collator::create} — Create a collator</li><li>{@link Collator::getAttribute} — Get collation attribute value</li><li>{@link Collator::getErrorCode} — Get collator's last error code</li><li>{@link Collator::getErrorMessage} — Get text for collator's last error code</li><li>{@link Collator::getLocale} — Get the locale name of the collator</li><li>{@link Collator::getSortKey} — Get sorting key for a string</li><li>{@link Collator::getStrength} — Get current collation strength</li><li>{@link Collator::setAttribute} — Set collation attribute</li><li>{@link Collator::setStrength} — Set collation strength</li><li>{@link Collator::sortWithSortKeys} — Sort array using specified collator and sort keys</li><li>{@link Collator::sort} — Sort array using specified collator</li></ul> <div class="up"><a href="http://php.net/manual/zh/book.intl.php">intl</a></div></div>
*/
class Collator {
    const DEFAULT_VALUE = -1;
    const PRIMARY = 0;
    const SECONDARY = 1;
    const TERTIARY = 2;
    const DEFAULT_STRENGTH = 2;
    const QUATERNARY = 3;
    const IDENTICAL = 15;
    const OFF = 16;
    const ON = 17;
    const SHIFTED = 20;
    const NON_IGNORABLE = 21;
    const LOWER_FIRST = 24;
    const UPPER_FIRST = 25;

    /**
     * <p>
     * Sort strings with different accents from the back of the string. This
     * attribute is automatically set to
     * On
     * for the French locales and a few others. Users normally would not need
     * to explicitly set this attribute. There is a string comparison
     * performance cost when it is set On,
     * but sort key length is unaffected. Possible values are:
     * <b>Collator::ON</b>
     * <b>Collator::OFF</b>(default)
     * <b>Collator::DEFAULT_VALUE</b>
     * </p>
     * <p>
     * FRENCH_COLLATION rules
     * <p>
     * F=OFF cote &lt; coté &lt; côte &lt; côté
     * F=ON cote &lt; côte &lt; coté &lt; côté
     * </p>
     * </p>
     * @link http://php.net/manual/en/intl.collator-constants.php
     */
    const FRENCH_COLLATION = 0;

    /**
     * <p>
     * The Alternate attribute is used to control the handling of the so called
     * variable characters in the UCA: whitespace, punctuation and symbols. If
     * Alternate is set to NonIgnorable
     * (N), then differences among these characters are of the same importance
     * as differences among letters. If Alternate is set to
     * Shifted
     * (S), then these characters are of only minor importance. The
     * Shifted value is often used in combination with
     * Strength
     * set to Quaternary. In such a case, whitespace, punctuation, and symbols
     * are considered when comparing strings, but only if all other aspects of
     * the strings (base letters, accents, and case) are identical. If
     * Alternate is not set to Shifted, then there is no difference between a
     * Strength of 3 and a Strength of 4. For more information and examples,
     * see Variable_Weighting in the
     * UCA.
     * The reason the Alternate values are not simply
     * On and Off
     * is that additional Alternate values may be added in the future. The UCA
     * option Blanked is expressed with Strength set to 3, and Alternate set to
     * Shifted. The default for most locales is NonIgnorable. If Shifted is
     * selected, it may be slower if there are many strings that are the same
     * except for punctuation; sort key length will not be affected unless the
     * strength level is also increased.
     * </p>
     * <p>
     * Possible values are:
     * <b>Collator::NON_IGNORABLE</b>(default)
     * <b>Collator::SHIFTED</b>
     * <b>Collator::DEFAULT_VALUE</b>
     * </p>
     * <p>
     * ALTERNATE_HANDLING rules
     * <p>
     * S=3, A=N di Silva &lt; Di Silva &lt; diSilva &lt; U.S.A. &lt; USA
     * S=3, A=S di Silva = diSilva &lt; Di Silva &lt; U.S.A. = USA
     * S=4, A=S di Silva &lt; diSilva &lt; Di Silva &lt; U.S.A. &lt; USA
     * </p>
     * </p>
     * @link http://php.net/manual/en/intl.collator-constants.php
     */
    const ALTERNATE_HANDLING = 1;

    /**
     * <p>
     * The Case_First attribute is used to control whether uppercase letters
     * come before lowercase letters or vice versa, in the absence of other
     * differences in the strings. The possible values are
     * Uppercase_First
     * (U) and Lowercase_First
     * (L), plus the standard Default
     * and Off.
     * There is almost no difference between the Off and Lowercase_First
     * options in terms of results, so typically users will not use
     * Lowercase_First: only Off or Uppercase_First. (People interested in the
     * detailed differences between X and L should consult the Collation
     * Customization). Specifying either L or U won't affect string comparison
     * performance, but will affect the sort key length.
     * </p>
     * <p>
     * Possible values are:
     * <b>Collator::OFF</b>(default)
     * <b>Collator::LOWER_FIRST</b>
     * <b>Collator::UPPER_FIRST</b>
     * <b>Collator:DEFAULT</b>
     * </p>
     * <p>
     * CASE_FIRST rules
     * <p>
     * C=X or C=L "china" &lt; "China" &lt; "denmark" &lt; "Denmark"
     * C=U "China" &lt; "china" &lt; "Denmark" &lt; "denmark"
     * </p>
     * </p>
     * @link http://php.net/manual/en/intl.collator-constants.php
     */
    const CASE_FIRST = 2;

    /**
     * <p>
     * The Case_Level attribute is used when ignoring accents but not case. In
     * such a situation, set Strength to be Primary,
     * and Case_Level to be On.
     * In most locales, this setting is Off by default. There is a small
     * string comparison performance and sort key impact if this attribute is
     * set to be On.
     * </p>
     * <p>
     * Possible values are:
     * <b>Collator::OFF</b>(default)
     * <b>Collator::ON</b>
     * <b>Collator::DEFAULT_VALUE</b>
     * </p>
     * <p>
     * CASE_LEVEL rules
     * <p>
     * S=1, E=X role = Role = rôle
     * S=1, E=O role = rôle &lt; Role
     * </p>
     * </p>
     * @link http://php.net/manual/en/intl.collator-constants.php
     */
    const CASE_LEVEL = 3;

    /**
     * <p>
     * The Normalization setting determines whether text is thoroughly
     * normalized or not in comparison. Even if the setting is off (which is
     * the default for many locales), text as represented in common usage will
     * compare correctly (for details, see UTN #5). Only if the accent marks
     * are in noncanonical order will there be a problem. If the setting is
     * On,
     * then the best results are guaranteed for all possible text input.
     * There is a medium string comparison performance cost if this attribute
     * is On,
     * depending on the frequency of sequences that require normalization.
     * There is no significant effect on sort key length. If the input text is
     * known to be in NFD or NFKD normalization forms, there is no need to
     * enable this Normalization option.
     * </p>
     * <p>
     * Possible values are:
     * <b>Collator::OFF</b>(default)
     * <b>Collator::ON</b>
     * <b>Collator::DEFAULT_VALUE</b>
     * </p>
     * @link http://php.net/manual/en/intl.collator-constants.php
     */
    const NORMALIZATION_MODE = 4;

    /**
     * <p>
     * The ICU Collation Service supports many levels of comparison (named
     * "Levels", but also known as "Strengths"). Having these categories
     * enables ICU to sort strings precisely according to local conventions.
     * However, by allowing the levels to be selectively employed, searching
     * for a string in text can be performed with various matching conditions.
     * For more detailed information, see
     * <b>collator_set_strength</b> chapter.
     * </p>
     * <p>
     * Possible values are:
     * <b>Collator::PRIMARY</b>
     * <b>Collator::SECONDARY</b>
     * <b>Collator::TERTIARY</b>(<default)
     * <b>Collator::QUATERNARY</b>
     * <b>Collator::IDENTICAL</b>
     * <b>Collator::DEFAULT_VALUE</b>
     * </p>
     * @link http://php.net/manual/en/intl.collator-constants.php
     */
    const STRENGTH = 5;

    /**
     * <p>
     * Compatibility with JIS x 4061 requires the introduction of an additional
     * level to distinguish Hiragana and Katakana characters. If compatibility
     * with that standard is required, then this attribute should be set
     * On,
     * and the strength set to Quaternary. This will affect sort key length
     * and string comparison string comparison performance.
     * </p>
     * <p>
     * Possible values are:
     * <b>Collator::OFF</b>(default)
     * <b>Collator::ON</b>
     * <b>Collator::DEFAULT_VALUE</b>
     * </p>
     * @link http://php.net/manual/en/intl.collator-constants.php
     */
    const HIRAGANA_QUATERNARY_MODE = 6;

    /**
     * <p>
     * When turned on, this attribute generates a collation key for the numeric
     * value of substrings of digits. This is a way to get '100' to sort AFTER
     * '2'.
     * </p>
     * <p>
     * Possible values are:
     * <b>Collator::OFF</b>(default)
     * <b>Collator::ON</b>
     * <b>Collator::DEFAULT_VALUE</b>
     * </p>
     * @link http://php.net/manual/en/intl.collator-constants.php
     */
    const NUMERIC_COLLATION = 7;
    const SORT_REGULAR = 0;
    const SORT_STRING = 1;
    const SORT_NUMERIC = 2;


    /**
     * (PHP 5 &gt;= 5.3.0, PECL intl &gt;= 1.0.0)<br/>
     * Create a collator
     * @link http://php.net/manual/en/collator.construct.php
     * @param string $locale
     */
    public function __construct($locale) { }

/**
*<div id="collator.create" class="refentry">  <div class="refnamediv">   <h1 class="refname">Collator::create</h1>   <h1 class="refname">collator_create</h1>   <p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7, PECL intl &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">Collator::create</span> -- <span class="refname">collator_create</span> &mdash; <span class="dc-title">Create a collator</span></p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-collator.create-description">   <h3 class="title">说明</h3>   <p class="para">面向对象风格</p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="modifier">static</span>    <span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.collator.php" class="type Collator" style="color:#EAB766">Collator</a></span>     <span class="methodname" style="color:#CC7832"><strong>Collator::create</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$locale</span></span>    )</div>    <p class="para rdfs-comment">    过程化风格   </p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.collator.php" class="type Collator" style="color:#EAB766">Collator</a></span>     <span class="methodname" style="color:#CC7832"><strong>collator_create</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$locale</span></span>    )</div>    <p class="para rdfs-comment">    The strings will be compared using the options already specified.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-collator.create-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">locale</span></dt>       <dd>        <p class="para">        The locale containing the required collation rules. Special values for        locales can be passed in - if null is passed for the locale, the        default locale collation rules will be used. If empty string (&quot;&quot;) or        &quot;root&quot; are passed, <a href="http://www.unicode.org/reports/tr10/" class="link external">&raquo;&nbsp;UCA</a> rules will be used.       </p>      </dd>          </dl>    </span>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-collator.create-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Return new instance of <a href="http://php.net/manual/zh/class.collator.php" class="classname">Collator</a> object, or <strong><span>NULL</span></strong>    on error.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-collator.create-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-2901">     <p><strong>Example #1 <span class="function"><strong style="color:#CC7832">collator_create()</strong></span> example</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$coll&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">collator_create</span><span style="color: #007700">(&nbsp;</span><span style="color: #DD0000">'en_US'&nbsp;</span><span style="color: #007700">);<br /><br />if(&nbsp;!isset(&nbsp;</span><span style="color: #9876AA">$coll&nbsp;</span><span style="color: #007700">)&nbsp;)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">printf</span><span style="color: #007700">(&nbsp;</span><span style="color: #DD0000">"Collator&nbsp;creation&nbsp;failed:&nbsp;%s\n"</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">intl_get_error_message</span><span style="color: #007700">()&nbsp;);<br />&nbsp;&nbsp;&nbsp;&nbsp;exit(&nbsp;</span><span style="color: #9876AA">1&nbsp;</span><span style="color: #007700">);<br />}<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-collator.create-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link Collator::__construct()} - Create a collator</span></li>    </ul>   </span>  </div>  <div class="up"><a href="http://php.net/manual/zh/class.collator.php">Collator</a></div></div>
     * @return Collator Return new instance of <b>Collator</b> object, or <b>NULL</b>*/
    public static function create($locale) { }

/**
*<div id="collator.compare" class="refentry">  <div class="refnamediv">   <h1 class="refname">Collator::compare</h1>   <h1 class="refname">collator_compare</h1>   <p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7, PECL intl &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">Collator::compare</span> -- <span class="refname">collator_compare</span> &mdash; <span class="dc-title">Compare two Unicode strings</span></p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-collator.compare-description">   <h3 class="title">说明</h3>   <p class="para">    面向对象风格   </p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">int</span>     <span class="methodname" style="color:#CC7832"><strong>Collator::compare</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$str1</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$str2</span></span>    )</div>    <p class="para rdfs-comment">    过程化风格   </p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">int</span>     <span class="methodname" style="color:#CC7832"><strong>collator_compare</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.collator.php" class="type Collator" style="color:#EAB766">Collator</a></span> <span class="parameter" style="color:#2EACF9">$coll</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$str1</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$str2</span></span>    )</div>    <p class="para rdfs-comment">    Compare two Unicode strings according to collation rules.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-collator.compare-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">coll</span></dt>       <dd>        <p class="para">        <a href="http://php.net/manual/zh/class.collator.php" class="classname">Collator</a> object.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">str1</span></dt>       <dd>        <p class="para">        The first string to compare.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">str2</span></dt>       <dd>        <p class="para">        The second string to compare.       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-collator.compare-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">Return comparison result:</p>   <p class="para">    <ul class="itemizedlist">     <li class="listitem">      <p class="para">       1 if <span class="parameter" style="color:#2EACF9">str1</span> is <em class="emphasis">greater</em> than        <span class="parameter" style="color:#2EACF9">str2</span> ;      </p>     </li>     <li class="listitem">      <p class="para">       0 if <span class="parameter" style="color:#2EACF9">str1</span> is <em class="emphasis">equal</em> to        <span class="parameter" style="color:#2EACF9">str2</span>;      </p>     </li>     <li class="listitem">      <p class="para">       -1 if <span class="parameter" style="color:#2EACF9">str1</span> is <em class="emphasis">less</em> than        <span class="parameter" style="color:#2EACF9">str2</span> .      </p>     </li>    </ul>    On error    <span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.boolean.php" class="type boolean" style="color:#EAB766">boolean</a></span>    <strong><span>FALSE</span></strong>    is returned.   </p>   <div class="warning"><strong class="warning">Warning</strong><p class="simpara">此函数可能返回布尔值 <strong><span>FALSE</span></strong>，但也可能返回等同于 <strong><span>FALSE</span></strong> 的非布尔值。请阅读 <a href="http://php.net/manual/zh/language.types.boolean.php" class="link">布尔类型</a>章节以获取更多信息。应使用 <a href="http://php.net/manual/zh/language.operators.comparison.php" class="link">=== 运算符</a>来测试此函数的返回值。</p></div>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-collator.compare-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-2899">     <p><strong>Example #1 <span class="function"><strong style="color:#CC7832">collator_compare()</strong></span>example</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$s1&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'Hello'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$s2&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'hello'</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$coll&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">collator_create</span><span style="color: #007700">(&nbsp;</span><span style="color: #DD0000">'en_US'&nbsp;</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$res&nbsp;&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">collator_compare</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$coll</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$s1</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$s2&nbsp;</span><span style="color: #007700">);<br /><br />if&nbsp;(</span><span style="color: #9876AA">$res&nbsp;</span><span style="color: #007700">===&nbsp;</span><span style="color: #9876AA">false</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #9876AA">collator_get_error_message</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$coll&nbsp;</span><span style="color: #007700">);<br />}&nbsp;else&nbsp;if(&nbsp;</span><span style="color: #9876AA">$res&nbsp;</span><span style="color: #007700">&gt;&nbsp;</span><span style="color: #9876AA">0&nbsp;</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"s1&nbsp;is&nbsp;greater&nbsp;than&nbsp;s2\n"</span><span style="color: #007700">;<br />}&nbsp;else&nbsp;if(&nbsp;</span><span style="color: #9876AA">$res&nbsp;</span><span style="color: #007700">&lt;&nbsp;</span><span style="color: #9876AA">0&nbsp;</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"s1&nbsp;is&nbsp;less&nbsp;than&nbsp;s2\n"</span><span style="color: #007700">;<br />}&nbsp;else&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"s1&nbsp;is&nbsp;equal&nbsp;to&nbsp;s2\n"</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>      <div class="example-contents"><p>以上例程会输出：</p></div>     <div class="example-contents screen" style="background:black;padding-left:5px;"><br />      s1 is greater than s2<br />     </div>    </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-collator.compare-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function"><a href="http://php.net/manual/zh/collator.sort.php" class="function" rel="rdfs-seeAlso">collator_sort()</a> - Sort array using specified collator</span></li>    </ul>   </span>  </div>  <div class="up"><a href="http://php.net/manual/zh/class.collator.php">Collator</a></div></div>
     * @return int Return comparison result:</p>*/
    public function compare($str1, $str2) { }

/**
*<div id="collator.sort" class="refentry">  <div class="refnamediv">   <h1 class="refname">Collator::sort</h1>   <h1 class="refname">collator_sort</h1>   <p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7, PECL intl &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">Collator::sort</span> -- <span class="refname">collator_sort</span> &mdash; <span class="dc-title">Sort array using specified collator</span></p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-collator.sort-description">   <h3 class="title">说明</h3>   <p class="para">    面向对象风格   </p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><strong>Collator::sort</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#2EACF9">&$arr</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$sort_flag</span></span>   ] )</div>    <p class="para rdfs-comment">    过程化风格   </p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><strong>collator_sort</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.collator.php" class="type Collator" style="color:#EAB766">Collator</a></span> <span class="parameter" style="color:#2EACF9">$coll</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#2EACF9">&$arr</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$sort_flag</span></span>   ] )</div>    <p class="para rdfs-comment">This function sorts an array according to current locale rules.</p>   <p class="para">    Equivalent to standard PHP    <span class="function">{@link sort()}</span> .   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-collator.sort-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">coll</span></dt>       <dd>        <p class="para">        <a href="http://php.net/manual/zh/class.collator.php" class="classname">Collator</a> object.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">arr</span></dt>       <dd>        <p class="para">        Array of strings to sort.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">sort_flag</span></dt>       <dd>        <p class="para">        Optional sorting type, one of the following:       </p>       <p class="para">        <ul class="itemizedlist">         <li class="listitem">          <p class="para">           <strong><span>Collator::SORT_REGULAR</span></strong>           - compare items normally (don&#039;t change types)          </p>         </li>         <li class="listitem">          <p class="para">           <strong><span>Collator::SORT_NUMERIC</span></strong>           - compare items numerically          </p>         </li>         <li class="listitem">          <p class="para">           <strong><span>Collator::SORT_STRING</span></strong>           - compare items as strings          </p>         </li>        </ul>        Default sorting type is <strong><span>Collator::SORT_REGULAR</span></strong>.        It is also used if an invalid <span class="parameter" style="color:#2EACF9">sort_flag</span> value has been specified.       </p>      </dd>          </dl>    </span>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-collator.sort-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-collator.sort-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-2911">     <p><strong>Example #1 <span class="function"><strong style="color:#CC7832">collator_sort()</strong></span> example</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$coll&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">collator_create</span><span style="color: #007700">(&nbsp;</span><span style="color: #DD0000">'en_US'&nbsp;</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$arr&nbsp;&nbsp;</span><span style="color: #007700">=&nbsp;array(&nbsp;</span><span style="color: #DD0000">'at'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'às'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'as'&nbsp;</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">var_export</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$arr&nbsp;</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">collator_sort</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$coll</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$arr&nbsp;</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">var_export</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$arr&nbsp;</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>      <div class="example-contents"><p>以上例程会输出：</p></div>     <div class="example-contents screen" style="background:black;padding-left:5px;"> <div class="cdata"><span> array (   0 =&gt; &#039;at&#039;,   1 =&gt; &#039;às&#039;,   2 =&gt; &#039;as&#039;, )array (   0 =&gt; &#039;as&#039;,   1 =&gt; &#039;às&#039;,   2 =&gt; &#039;at&#039;, ) </span></div>     </div>    </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-collator.sort-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><a href="http://php.net/manual/zh/class.collator.php#intl.collator-constants" class="link"><a href="http://php.net/manual/zh/class.collator.php" class="classname">Collator</a> constants</a></li>     <li class="member"><span class="function"><a href="http://php.net/manual/zh/collator.asort.php" class="function" rel="rdfs-seeAlso">collator_asort()</a> - Sort array maintaining index association</span></li>     <li class="member"><span class="function"><a href="http://php.net/manual/zh/collator.sortwithsortkeys.php" class="function" rel="rdfs-seeAlso">collator_sort_with_sort_keys()</a> - Sort array using specified collator and sort keys</span></li>    </ul>   </span>  </div>  <div class="up"><a href="http://php.net/manual/zh/class.collator.php">Collator</a></div></div>
     * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
    public function sort(array &$arr, $sort_flag = null) { }

/**
*<div id="collator.sortwithsortkeys" class="refentry">  <div class="refnamediv">   <h1 class="refname">Collator::sortWithSortKeys</h1>   <h1 class="refname">collator_sort_with_sort_keys</h1>   <p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7, PECL intl &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">Collator::sortWithSortKeys</span> -- <span class="refname">collator_sort_with_sort_keys</span> &mdash; <span class="dc-title">Sort array using specified collator and sort keys</span></p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-collator.sortwithsortkeys-description">   <h3 class="title">说明</h3>   <p class="para">    面向对象风格   </p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><strong>Collator::sortWithSortKeys</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#2EACF9">&$arr</span></span>    )</div>    <p class="para rdfs-comment">    过程化风格   </p>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832"><strong>collator_sort_with_sort_keys</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.collator.php" class="type Collator" style="color:#EAB766">Collator</a></span> <span class="parameter" style="color:#2EACF9">$coll</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#2EACF9">&$arr</span></span>    )</div>    <p class="para rdfs-comment">    Similar to <span class="function"><a href="http://php.net/manual/zh/collator.sort.php" class="function">collator_sort()</a></span>    but uses ICU sorting keys produced by ucol_getSortKey() to gain more speed    on large arrays.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-collator.sortwithsortkeys-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">coll</span></dt>       <dd>        <p class="para">        <a href="http://php.net/manual/zh/class.collator.php" class="classname">Collator</a> object.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">arr</span></dt>       <dd>        <p class="para">Array of strings to sort</p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-collator.sortwithsortkeys-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-collator.sortwithsortkeys-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-2910">     <p><strong>Example #1 <span class="function"><strong style="color:#CC7832">collator_sort_with_sort_keys()</strong></span> example</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$arr&nbsp;&nbsp;</span><span style="color: #007700">=&nbsp;array(&nbsp;</span><span style="color: #DD0000">'Köpfe'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'Kypper'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'Kopfe'&nbsp;</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$coll&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">collator_create</span><span style="color: #007700">(&nbsp;</span><span style="color: #DD0000">'sv'&nbsp;</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">collator_sort_with_sort_keys</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$coll</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$arr&nbsp;</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">var_export</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$arr&nbsp;</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>      <div class="example-contents"><p>以上例程会输出：</p></div>     <div class="example-contents screen" style="background:black;padding-left:5px;"> <div class="cdata"><span> array (   0 =&gt; &#039;Kopfe&#039;,   1 =&gt; &#039;Kypper&#039;,   2 =&gt; &#039;Köpfe&#039;, ) </span></div>     </div>    </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-collator.sortwithsortkeys-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><a href="http://php.net/manual/zh/class.collator.php#intl.collator-constants" class="link"><a href="http://php.net/manual/zh/class.collator.php" class="classname">Collator</a> constants</a></li>     <li class="member"><span class="function"><a href="http://php.net/manual/zh/collator.sort.php" class="function" rel="rdfs-seeAlso">collator_sort()</a> - Sort array using specified collator</span></li>     <li class="member"><span class="function"><a href="http://php.net/manual/zh/collator.asort.php" class="function" rel="rdfs-seeAlso">collator_asort()</a> - Sort array maintaining index association</span></li>    </ul>   </span>  </div>  <div class="up"><a href="http://php.net/manual/zh/class.collator.php">Collator</a></div></div>
