><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />ini_set</span><span style="color: #007700">(</span><span style="color: #DD0000">'date.timezone'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'UTC'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">ini_set</span><span style="color: #007700">(</span><span style="color: #DD0000">'intl.default_locale'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'it_IT'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$cal&nbsp;</span><span style="color: #007700">=&nbsp;new&nbsp;</span><span style="color: #9876AA">IntlGregorianCalendar</span><span style="color: #007700">(</span><span style="color: #9876AA">2013</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">3&nbsp;</span><span style="color: #FF8000">//&nbsp;April&nbsp;</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">6</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$cal</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getLeastMaximum</span><span style="color: #007700">(</span><span style="color: #9876AA">IntlCalendar</span><span style="color: #007700">::</span><span style="color: #9876AA">FIELD_DAY_OF_MONTH</span><span style="color: #007700">),&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;28<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$cal</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getActualMaximum</span><span style="color: #007700">(</span><span style="color: #9876AA">IntlCalendar</span><span style="color: #007700">::</span><span style="color: #9876AA">FIELD_DAY_OF_MONTH</span><span style="color: #007700">),&nbsp;</span><span style="color: #FF8000">//&nbsp;30<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$cal</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getMaximum</span><span style="color: #007700">(</span><span style="color: #9876AA">IntlCalendar</span><span style="color: #007700">::</span><span style="color: #9876AA">FIELD_DAY_OF_MONTH</span><span style="color: #007700">)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;31<br /></span><span style="color: #007700">);</span> </span> </span></div>     </div>      <div class="example-contents"><p>以上例程会输出：</p></div>     <div class="example-contents screen" style="background:black;padding-left:5px;"> <div class="cdata"><span> int(28) int(30) int(31) </span></div>     </div>    </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-intlcalendar.getleastmaximum-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="methodname" style="color:#CC7832">{@link IntlCalendar::getActualMaximum()} - The maximum value for a field, considering the objectʼs current time</span></li>     <li class="member"><span class="methodname" style="color:#CC7832">{@link IntlCalendar::getMaximum()} - Get the global maximum value for a field</span></li>     <li class="member"><span class="methodname" style="color:#CC7832">{@link IntlCalendar::getGreatestMinimum()} - Get the largest local minimum value for a field</span></li>    </ul>   </span>  </div>  <div class="up"><a href="http://php.net/manual/zh/class.intlcalendar.php">IntlCalendar</a></div></div>
