/span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">substr</span><span style="color: #007700">(</span><span style="color: #9876AA">$key</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">0</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">mcrypt_enc_get_key_size</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">));<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$iv_size&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">mcrypt_enc_get_iv_size</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$iv&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">mcrypt_create_iv</span><span style="color: #007700">(</span><span style="color: #9876AA">$iv_size</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">MCRYPT_RAND</span><span style="color: #007700">);<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;初始化加密句柄&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #007700">if&nbsp;(</span><span style="color: #9876AA">mcrypt_generic_init</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$key</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$iv</span><span style="color: #007700">)&nbsp;!=&nbsp;-</span><span style="color: #9876AA">1</span><span style="color: #007700">)&nbsp;{<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;加密数据&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$c_t&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">mcrypt_generic</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$plain_text</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">mcrypt_generic_deinit</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">);<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;为解密重新初始化缓冲区&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">mcrypt_generic_init</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$key</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$iv</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$p_t&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">mdecrypt_generic</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$c_t</span><span style="color: #007700">);<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;执行清理工作&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">mcrypt_generic_deinit</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">mcrypt_module_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$td</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;}<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;if&