function oci_fetch_row ($statement) {}

/**
*<div id="function.oci-fetch-assoc" class="refentry">  <div class="refnamediv">   <h1 class="refname">oci_fetch_assoc</h1>   <p class="verinfo">(PHP 5, PHP 7, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_fetch_assoc</span> &mdash; <span class="dc-title">Returns the next row from a query as an associative array</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-fetch-assoc-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">array</span> <span class="methodname" style="color:#CC7832"><strong>oci_fetch_assoc</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$statement</span></span>    )</div>    <p class="para rdfs-comment">    Returns an associative array containing the next result-set row of a query.    Each array entry corresponds to a column of the row.  This function    is typically called in a loop until it returns <strong><span>FALSE</span></strong>, indicating    no more rows exist.   </p>   <p class="para">    Calling <span class="function"><strong style="color:#CC7832">oci_fetch_assoc()</strong></span> is identical to    calling <span class="function">{@link oci_fetch_array()}</span>    with <strong><span>OCI_ASSOC</span></strong>    + <strong><span>OCI_RETURN_NULLS</span></strong>.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-fetch-assoc-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">statement</span></dt>       <dd>         <p class="para">有效的 OCI8 报表标识符 由 <span class="function">{@link oci_parse()}</span> 创建，被 <span class="function">{@link oci_execute()}</span> 或 <em>REF CURSOR</em> statement 标识执行。</p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-fetch-assoc-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns an associative array. If there are no more rows in    the <span class="parameter" style="color:#2EACF9">statement</span> then <strong><span>FALSE</span></strong> is returned.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-fetch-assoc-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-2425">     <p><strong>Example #1 <span class="function"><strong style="color:#CC7832">oci_fetch_assoc()</strong></span> Example</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /><br />$conn&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br />if&nbsp;(!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$e&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">],&nbsp;</span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'SELECT&nbsp;department_id,&nbsp;department_name&nbsp;FROM&nbsp;departments'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br />while&nbsp;((</span><span style="color: #9876AA">$row&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_fetch_assoc</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">))&nbsp;!=&nbsp;</span><span style="color: #9876AA">false</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'DEPARTMENT_ID'</span><span style="color: #007700">]&nbsp;.&nbsp;</span><span style="color: #DD0000">"&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'DEPARTMENT_NAME'</span><span style="color: #007700">]&nbsp;.&nbsp;</span><span style="color: #DD0000">"&lt;br&gt;\n"</span><span style="color: #007700">;<br />}<br /><br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-fetch-assoc-notes">   <h3 class="title">注释</h3>   <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>:     <p class="para">     See <span class="function">{@link oci_fetch_array()}</span> for more examples of fetching rows.    </p>   </p></blockquote>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-fetch-assoc-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link oci_fetch()} - Fetches the next row into result-buffer</span></li>     <li class="member"><span class="function">{@link oci_fetch_all()} - 获取结果数据的所有行到一个数组</span></li>     <li class="member"><span class="function">{@link oci_fetch_array()} - Returns the next row from a query as an associative or numeric array</span></li>     <li class="member"><span class="function">{@link oci_fetch_object()} - Returns the next row from a query as an object</span></li>     <li class="member"><span class="function">{@link oci_fetch_row()} - Returns the next row from a query as a numeric array</span></li>    </ul>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.oci8.php">OCI8 函数</a></div></div>
 * @return array an associative array. If there are no more rows in*/
function oci_fetch_assoc ($statement) {}

/**
*<div id="function.oci-fetch-array" class="refentry">  <div class="refnamediv">   <h1 class="refname">oci_fetch_array</h1>   <p class="verinfo">(PHP 5, PHP 7, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_fetch_array</span> &mdash; <span class="dc-title">Returns the next row from a query as an associative or numeric array   </span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-fetch-array-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">array</span> <span class="methodname" style="color:#CC7832"><strong>oci_fetch_array</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$statement</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$mode</span></span>   ] )</div>    <p class="para rdfs-comment">    Returns an array containing the next result-set row of a query.    Each array entry corresponds to a column of the row.  This function    is typically called in a loop until it returns <strong><span>FALSE</span></strong>, indicating    no more rows exist.   </p>   <p class="para">    If <span class="parameter" style="color:#2EACF9">statement</span> corresponds to a PL/SQL block    returning Oracle Database 12c Implicit Result Sets, then rows from    all sets are consecutively fetched. If    <span class="parameter" style="color:#2EACF9">statement</span> is returned    by <span class="function">{@link oci_get_implicit_resultset()}</span>, then only the    subset of rows for one child query are returned.   </p>   <p class="para">要获取 OCI8 扩展进行数据类型映射的细节，请参见<a href="http://php.net/manual/zh/oci8.datatypes.php" class="link">驱动所支持的数据类型</a>。</p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-fetch-array-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">statement</span></dt>       <dd>         <p class="para">有效的 OCI8 报表标识符 由 <span class="function">{@link oci_parse()}</span> 创建，被 <span class="function">{@link oci_execute()}</span> 或 <em>REF CURSOR</em> statement 标识执行。</p>        <p class="para">         Can also be a statement identifier returned by <span class="function">{@link oci_get_implicit_resultset()}</span>.        </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">mode</span></dt>       <dd>        <p class="para">        An optional second parameter can be any combination of the following        constants:        <table class="doctable table">         <caption><strong><span class="function"><strong style="color:#CC7832">oci_fetch_array()</strong></span> Modes</strong></caption>                   <thead>           <tr>            <th>Constant</th>            <th>Description</th>           </tr>           </thead>           <tbody class="tbody">           <tr>            <td><strong><span>OCI_BOTH</span></strong></td>            <td>Returns an array with both associative and numeric             indices. This is the same             as <strong><span>OCI_ASSOC</span></strong>             + <strong><span>OCI_NUM</span></strong> and is the default             behavior.</td>           </tr>            <tr>            <td><strong><span>OCI_ASSOC</span></strong></td>            <td>Returns an associative array.</td>           </tr>            <tr>            <td><strong><span>OCI_NUM</span></strong></td>            <td>Returns a numeric array.</td>           </tr>            <tr>            <td><strong><span>OCI_RETURN_NULLS</span></strong></td>            <td>Creates elements for <strong><span>NULL</span></strong> fields.  The element              values will be a PHP <strong><span>NULL</span></strong>.            </td>           </tr>            <tr>            <td><strong><span>OCI_RETURN_LOBS</span></strong></td>            <td>Returns the contents of LOBs instead of the LOB              descriptors.</td>           </tr>           </tbody>                 </table>        </p>       <p class="para">        The default <span class="parameter" style="color:#2EACF9">mode</span> is <strong><span>OCI_BOTH</span></strong>.       </p>       <p class="para">        Use the addition operator &quot;+&quot; to specify more than        one mode at a time.       </p>      </dd>          </dl>     </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-fetch-array-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns an array with associative and/or numeric indices. If there    are no more rows in the <span class="parameter" style="color:#2EACF9">statement</span> then    <strong><span>FALSE</span></strong> is returned.   </p>   <p class="para">     By default, <em>LOB</em> columns are returned as LOB descriptors.   </p>   <p class="para">    <em>DATE</em> columns are returned as strings formatted    to the current date format.  The default format can be changed with    Oracle environment variables such as <em>NLS_LANG</em> or    by a previously executed <em>ALTER SESSION SET    NLS_DATE_FORMAT</em> command.   </p>   <p class="para">    Oracle&#039;s default, non-case sensitive column names will have    uppercase associative indices in the result array.  Case-sensitive    column names will have array indices using the exact column case.    Use <span class="function">{@link var_dump()}</span> on the result array to verify the    appropriate case to use for each query.    </p>   <p class="para">    The table name is not included in the array index.  If your query    contains two different columns with the same name,    use <strong><span>OCI_NUM</span></strong> or add a column alias to the query    to ensure name uniqueness, see example #7. Otherwise only one    column will be returned via PHP.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-fetch-array-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-2414">     <p><strong>Example #1 <span class="function"><strong style="color:#CC7832">oci_fetch_array()</strong></span> with <strong><span>OCI_BOTH</span></strong></strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /><br />$conn&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br />if&nbsp;(!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$e&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">],&nbsp;</span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'SELECT&nbsp;department_id,&nbsp;department_name&nbsp;FROM&nbsp;departments'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br />while&nbsp;((</span><span style="color: #9876AA">$row&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">OCI_BOTH</span><span style="color: #007700">))&nbsp;!=&nbsp;</span><span style="color: #9876AA">false</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;Use&nbsp;the&nbsp;uppercase&nbsp;column&nbsp;names&nbsp;for&nbsp;the&nbsp;associative&nbsp;array&nbsp;indices<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #007700">echo&nbsp;</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">0</span><span style="color: #007700">]&nbsp;.&nbsp;</span><span style="color: #DD0000">"&nbsp;and&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'DEPARTMENT_ID'</span><span style="color: #007700">]&nbsp;&nbsp;&nbsp;.&nbsp;</span><span style="color: #DD0000">"&nbsp;are&nbsp;the&nbsp;same&lt;br&gt;\n"</span><span style="color: #007700">;<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">1</span><span style="color: #007700">]&nbsp;.&nbsp;</span><span style="color: #DD0000">"&nbsp;and&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'DEPARTMENT_NAME'</span><span style="color: #007700">]&nbsp;.&nbsp;</span><span style="color: #DD0000">"&nbsp;are&nbsp;the&nbsp;same&lt;br&gt;\n"</span><span style="color: #007700">;<br />}<br /><br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>   </span>   <p class="para">    <div class="example" id="example-2415">     <p><strong>Example #2 <span class="function"><strong style="color:#CC7832">oci_fetch_array()</strong></span> with <strong><span>OCI_NUM</span></strong></strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">//<br />&nbsp;&nbsp;Before&nbsp;running,&nbsp;create&nbsp;the&nbsp;table:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;CREATE&nbsp;TABLE&nbsp;mytab&nbsp;(id&nbsp;NUMBER,&nbsp;description&nbsp;CLOB);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;INSERT&nbsp;INTO&nbsp;mytab&nbsp;(id,&nbsp;description)&nbsp;values&nbsp;(1,&nbsp;'A&nbsp;very&nbsp;long&nbsp;string');<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;COMMIT;<br /><br /><br /></span><span style="color: #9876AA">$conn&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br />if&nbsp;(!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$e&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">],&nbsp;</span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">),&nbsp;</span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'SELECT&nbsp;id,&nbsp;description&nbsp;FROM&nbsp;mytab'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br />while&nbsp;((</span><span style="color: #9876AA">$row&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">oci_fetch_ar