bsp;</span><span style="color: #9876AA">$ciphers</span><span style="color: #007700">,&nbsp;function(</span><span style="color: #9876AA">$n</span><span style="color: #007700">)&nbsp;{&nbsp;return&nbsp;</span><span style="color: #9876AA">stripos</span><span style="color: #007700">(</span><span style="color: #9876AA">$n</span><span style="color: #007700">,</span><span style="color: #DD0000">"ecb"</span><span style="color: #007700">)===</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">;&nbsp;}&nbsp;);<br /><br /></span><span style="color: #FF8000">//At&nbsp;least&nbsp;as&nbsp;early&nbsp;as&nbsp;Aug&nbsp;2016,&nbsp;Openssl&nbsp;declared&nbsp;the&nbsp;following&nbsp;weak:&nbsp;RC2,&nbsp;RC4,&nbsp;DES,&nbsp;3DES,&nbsp;MD5&nbsp;based<br /></span><span style="color: #9876AA">$ciphers&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">array_filter</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$ciphers</span><span style="color: #007700">,&nbsp;function(</span><span style="color: #9876AA">$c</span><span style="color: #007700">)&nbsp;{&nbsp;return&nbsp;</span><span style="color: #9876AA">stripos</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">,</span><span style="color: #DD0000">"des"</span><span style="color: #007700">)===</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">;&nbsp;}&nbsp;);<br /></span><span style="color: #9876AA">$ciphers&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">array_filter</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$ciphers</span><span style="color: #007700">,&nbsp;function(</span><span style="color: #9876AA">$c</span><span style="color: #007700">)&nbsp;{&nbsp;return&nbsp;</span><span style="color: #9876AA">stripos</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">,</span><span style="color: #DD0000">"rc2"</span><span style="color: #007700">)===</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">;&nbsp;}&nbsp;);<br /></span><span style="color: #9876AA">$ciphers&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">array_filter</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$ciphers</span><span style="color: #007700">,&nbsp;function(</span><span style="color: #9876AA">$c</span><span style="color: #007700">)&nbsp;{&nbsp;return&nbsp;</span><span style="color: #9876AA">stripos</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">,</span><span style="color: #DD0000">"rc4"</span><span style="color: #007700">)===</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">;&nbsp;}&nbsp;);<br /></span><span style="color: #9876AA">$ciphers&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">array_filter</span><span style="color: #007700">(&nbsp;</span><span style="color: #9876AA">$ciphers</span><span style="color: #007700">,&nbsp;function(</span><span style="color: #9876AA">$c</span><span style="color: #007700">)&nbsp;{&nbsp;return&nbsp;</span><span style="color: #9876AA">stripos</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">,</span><span style="color: #DD0000">"md5"</span><span style="color: #007700">)===</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">;&nbsp;}&nbsp;);<br /></span><span style="color: #9876AA">$cipher_aliases&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">array_filter</span><span style="color: #007700">(</span><span style="color: #9876AA">$cipher_aliases</span><span style="color: #007700">,function(</span><span style="color: #9876AA">$c</span><span style="color: #007700">)&nbsp;{&nbsp;re