nect</span><span style="color: #007700">(</span><span style="color: #DD0000">"dbname=jacarta"</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">pg_query</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"begin"</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">$oid&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">pg_lo_import</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'/tmp/lob.dat'</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;</span><span style="color: #9876AA">pg_query</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"commit"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>    </span>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.pg-lo-import-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link pg_lo_export()} - 将大型对象导出到文件</span></li>     <li class="member"><span class="function">{@link pg_lo_open()} - 打开一个大型对象</span></li>    </ul>   </span>  </div>    <div class="up"><a href="http://php.net/manual/zh/ref.pgsql.php">PostgreSQL 函数</a></div></div>
