function snmpget ($hostname, $community, $object_id, $timeout = 1000000, $retries = 5) {}

/**
*<div id="function.snmpgetnext" class="refentry">  <div class="refnamediv">   <h1 class="refname">snmpgetnext</h1>   <p class="verinfo">(PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">snmpgetnext</span> &mdash; <span class="dc-title">    Fetch the <acronym title="Simple Network Management Protocol">SNMP</acronym> object which follows the given object id   </span></p>   </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.snmpgetnext-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">string</span> <span class="methodname" style="color:#CC7832"><strong>snmpgetnext</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$host</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$community</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$object_id</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$timeout</span><span class="initializer"> = 1000000</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$retries</span><span class="initializer"> = 5</span></span>   ]] )</div>       <p class="para rdfs-comment">    The <span class="function"><strong style="color:#CC7832">snmpgetnext()</strong></span> function is used to read the    value of the <acronym title="Simple Network Management Protocol">SNMP</acronym> object that follows the specified     <span class="parameter" style="color:#2EACF9">object_id</span>.   </p>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.snmpgetnext-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">host</span></dt>       <dd> <p class="para">The hostname of the <acronym title="Simple Network Management Protocol">SNMP</acronym> agent (server).</p></dd>                 <dt> <span class="parameter" style="color:#2EACF9">community</span></dt>       <dd> <p class="para">The read community.</p></dd>                 <dt> <span class="parameter" style="color:#2EACF9">object_id</span></dt>       <dd> <p class="para">The <acronym title="Simple Network Management Protocol">SNMP</acronym> object id which precedes the wanted one.</p></dd>                 <dt> <span class="parameter" style="color:#2EACF9">timeout</span></dt>       <dd> <p class="para">The number of microseconds until the first timeout.</p></dd>                 <dt> <span class="parameter" style="color:#2EACF9">retries</span></dt>       <dd> <p class="para">The number of times to retry if timeouts occur.</p></dd>          </dl>    </span>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.snmpgetnext-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns <acronym title="Simple Network Management Protocol">SNMP</acronym> object value on success or <strong><span>FALSE</span></strong> on error.    In case of an error, an E_WARNING message is shown.   </p>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.snmpgetnext-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-5571">     <p><strong>Example #1 Using <span class="function"><strong style="color:#CC7832">snmpgetnext()</strong></span></strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$nameOfSecondInterface&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">snmpgetnetxt</span><span style="color: #007700">(</span><span style="color: #DD0000">'localhost'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'public'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'IF-MIB::ifName.1'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>   </span>  </div>     <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.snmpgetnext-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link snmpget()} - 获取一个 SNMP 对象</span></li>     <li class="member"><span class="function">{@link snmpwalk()} - 从代理返回所有的 SNMP 对象</span></li>    </ul>   </span>  </div>    <div class="up"><a href="http://php.net/manual/zh/ref.snmp.php">SNMP 函数</a></div></div>
