>);<br /><br /></span><span style="color: #9876AA">ssh2_sftp_rename</span><span style="color: #007700">(</span><span style="color: #9876AA">$sftp</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'/home/username/oldname'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'/home/username/newname'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>      </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.ssh2-sftp-rename-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link rename()} - 重命名一个文件或目录</span></li>    </ul>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.ssh2.php">SSH2 函数</a></div></div>
