an><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ssh2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'shell.example.com'</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">22</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">ssh2_auth_password</span><span style="color: #007700">(</span><span style="color: #9876AA">$connection</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'username'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'password'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$sftp&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ssh2_sftp</span><span style="color: #007700">(</span><span style="color: #9876AA">$connection</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">ssh2_sftp_mkdir</span><span style="color: #007700">(</span><span style="color: #9876AA">$sftp</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'/home/username/newdir'</span><span style="color: #007700">);<br /></span><span style="color: #FF8000">//&nbsp;Or:&nbsp;&nbsp;mkdir("ssh2.sftp://$sftp/home/username/newdir");&nbsp;<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>      </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.ssh2-sftp-mkdir-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link mkdir()} - 新建目录</span></li>     <li class="member"><span class="function">{@link ssh2_sftp_rmdir()} - Remove a directory</span></li>    </ul>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.ssh2.php">SSH2 函数</a></div></div>
