/**
*<div id="function.ssh2-publickey-list" class="refentry">  <div class="refnamediv">   <h1 class="refname">ssh2_publickey_list</h1>   <p class="verinfo">(PECL ssh2 &gt;= 0.10)</p><p class="refpurpose"><span class="refname">ssh2_publickey_list</span> &mdash; <span class="dc-title">    List currently authorized publickeys   </span></p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.ssh2-publickey-list-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">array</span> <span class="methodname" style="color:#CC7832"><strong>ssh2_publickey_list</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$pkey</span></span>    )</div>    <p class="para rdfs-comment">    List currently authorized publickeys.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.ssh2-publickey-list-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">pkey</span></dt>       <dd>        <p class="para">        Publickey Subsystem resource       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.ssh2-publickey-list-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns a numerically indexed array of keys,    each of which is an associative array containing:    name, blob, and attrs elements.   </p>   <p class="para">    <table class="doctable table">     <caption><strong>Publickey elements</strong></caption>           <thead>       <tr>        <th>Array Key</th>        <th>Meaning</th>       </tr>       </thead>       <tbody class="tbody">       <tr>        <td>name</td>        <td>Name of algorithm used by this publickey, for example:        <em>ssh-dss</em> or <em>ssh-rsa</em>.</td>       </tr>        <tr>        <td>blob</td>        <td>Publickey blob as raw binary data.</td>       </tr>        <tr>        <td>attrs</td>        <td>Attributes assigned to this publickey. The most common        attribute, and the only one supported by publickey version 1        servers, is <em>comment</em>, which may be any freeform        string.</td>       </tr>       </tbody>         </table>    </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.ssh2-publickey-list-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-5616">     <p><strong>Example #1 Listing authorized keys with <span class="function"><strong style="color:#CC7832">ssh2_publickey_list()</strong></span></strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$ssh2&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ssh2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'shell.example.com'</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">22</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">ssh2_auth_password</span><span style="color: #007700">(</span><span style="color: #9876AA">$ssh2</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'jdoe'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'secret'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$pkey&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ssh2_publickey_init</span><span style="color: #007700">(</span><span style="color: #9876AA">$ssh2</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$list&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ssh2_publickey_list</span><span style="color: #007700">(</span><span style="color: #9876AA">$pkey</span><span style="color: #007700">);<br /><br />foreach(</span><span style="color: #9876AA">$list&nbsp;</span><span style="color: #007700">as&nbsp;</span><span style="color: #9876AA">$key</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"Key:&nbsp;</span><span style="color: #007700">{</span><span style="color: #9876AA">$key</span><span style="color: #007700">[</span><span style="color: #DD0000">'name'</span><span style="color: #007700">]}</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br />&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"Blob:&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">chunk_split</span><span style="color: #007700">(</span><span style="color: #9876AA">base64_encode</span><span style="color: #007700">(</span><span style="color: #9876AA">$key</span><span style="color: #007700">[</span><span style="color: #DD0000">'blob'</span><span style="color: #007700">]),&nbsp;</span><span style="color: #9876AA">40</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">"\n"</span><span style="color: #007700">)&nbsp;.&nbsp;</span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"Comment:&nbsp;</span><span style="color: #007700">{</span><span style="color: #9876AA">$key</span><span style="color: #007700">[</span><span style="color: #DD0000">'attrs'</span><span style="color: #007700">][</span><span style="color: #DD0000">'comment'</span><span style="color: #007700">]}</span><span style="color: #DD0000">\n\n"</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>      <div class="example-contents"><p>以上例程会输出：</p></div>     <div class="example-contents screen" style="background:black;padding-left:5px;"> <div class="cdata"><span> Key: ssh-rsa Blob: AAAAB3NzaC1yc2EAAAABIwAAAIEA5HVt6VqSGd5P TrLRdjNONxXH1tVFGn0Bd26BF0aCP9qyJRlvdJ3j 4WBeX4ZmrveGrjMgkseSYc4xZ26sDHwfL351xjza Lpipu\BGRrw17mWVBhuCExo476ri5tQFzbTc54VE HYckxQ16CjSTibI5X69GmnYC9PNqEYq/1TP+HF10 Comment: John&#039;s Key  Key: ssh-rsa Blob: AAAAB3NzaHVt6VqSGd5C1yc2EAAAABIwA232dnJA AIEA5HVt6VqSGd5PTrLRdjNONxX/1TP+HF1HVt6V qSGd50H1tVFGn0BB3NzaC1yc2EAd26BF0aCP9qyJ RlvdJ3j4WBeX4ZmrveGrjMgkseSYc4xZ26HVt6Vq SGd5sDHwfL351xjzaLpipu\BGB3NzaC1yc2EA/1T Comment: Alice&#039;s Key  </span></div>     </div>    </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.ssh2-publickey-list-notes">   <h3 class="title">注释</h3>   <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>: <span class="simpara">公钥子系统用于管理服务器到<em class="emphasis">已</em>被认证的客户端的公钥。要用公钥认证方法来认证远程系统，应使用 <span class="function">{@link ssh2_auth_pubkey_file()}</span> 函数。</span></p></blockquote>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.ssh2-publickey-list-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link ssh2_publickey_init()} - Initialize Publickey subsystem</span></li>     <li class="member"><span class="function">{@link ssh2_publickey_add()} - Add an authorized publickey</span></li>     <li class="member"><span class="function">{@link ssh2_publickey_remove()} - Remove an authorized publickey</span></li>    </ul>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.ssh2.php">SSH2 函数</a></div></div>
 * @return array a numerically indexed array of keys,*/
function ssh2_publickey_list ($pkey) {}

/**
*<div id="function.ssh2-sftp-chmod" class="refentry">  <div class="refnamediv">   <h1 class="refname">ssh2_sftp_chmod</h1>   <p class="verinfo">(PECL ssh2 &gt;= 0.12)</p><p class="refpurpose"><span class="refname">ssh2_sftp_chmod</span> &mdash; <span class="dc-title">Changes file mode</span></p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.ssh2-sftp-chmod-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><strong>ssh2_sftp_chmod</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$sftp</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$filename</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$mode</span></span>    )</div>    <p class="para rdfs-comment">    Attempts to change the mode of the specified file to that given in    <span class="parameter" style="color:#2EACF9">mode</span>.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.ssh2-sftp-chmod-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">sftp</span></dt>       <dd>        <p class="para">        An SSH2 SFTP resource opened by <span class="function">{@link ssh2_sftp()}</span>.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">filename</span></dt>       <dd>        <p class="para">        Path to the file.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">mode</span></dt>       <dd>        <p class="para">        Permissions on the file. See the <span class="function">{@link chmod()}</span> for more details on this parameter.       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.ssh2-sftp-chmod-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.ssh2-sftp-chmod-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-5619">     <p><strong>Example #1 Changing the mode of a file on a remote server</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$connection&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ssh2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'shell.example.com'</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">22</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">ssh2_auth_password</span><span style="color: #007700">(</span><span style="color: #9876AA">$connection</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'username'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'password'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$sftp&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ssh2_sftp</span><span style="color: #007700">(</span><span style="color: #9876AA">$connection</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">ssh2_sftp_chmod</span><span style="color: #007700">(</span><span style="color: #9876AA">$sftp</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'/somedir/somefile'</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">0755</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>   </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.ssh2-sftp-chmod-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link chmod()} - 改变文件模式</span></li>     <li class="member"><span class="function">{@link ssh2_sftp()} - Initialize SFTP subsystem</span></li>     <li class="member"><span class="function">{@link ssh2_connect()} - Connect to an SSH server</span></li>    </ul>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.ssh2.php">SSH2 函数</a></div></div>
 * @return bool <p>Returns TRUE on success or FALSE on failure.</p>*/
function ssh2_sftp_chmod ($sftp, $filename, $mode) {}

/**
*<div id="function.ssh2-auth-agent" class="refentry">  <div class="refnamediv">   <h1 class="refname">ssh2_auth_agent</h1>   <p class="verinfo">(PECL ssh2 &gt;= 0.12)</p><p class="refpurpose"><span class="refname">ssh2_auth_agent</span> &mdash; <span class="dc-title">Authenticate over SSH using the ssh agent</span></p>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.ssh2-auth-agent-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><strong>ssh2_auth_agent</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$session</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#2EACF9">$username</span></span>    )</div>    <p class="para rdfs-comment">    Authenticate over SSH using the ssh agent   </p>   <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">Note</strong>:      <span class="simpara">      The <span class="function"><strong style="color:#CC7832">ssh2_auth_agent()</strong></span> function will only be available       when the ssh2 extension is compiled with libssh &gt;= 1.2.3.     </span>   </p></blockquote>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.ssh2-auth-agent-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">session</span></dt>       <dd>        <p class="para">        An SSH connection link identifier, obtained from a call to        <span class="function">{@link ssh2_connect()}</span>.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">username</span></dt>       <dd>        <p class="para">        Remote user name.       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.ssh2-auth-agent-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.ssh2-auth-agent-examples">   <h3 class="title">范例</h3>   <span>    <div class="example" id="example-5605">     <p><strong>Example #1 Authenticating with a ssh agent</strong></p>     <div class="example-contents"> <div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$connection&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">ssh2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'shell.example.com'</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">22</span><span style="color: #007700">);<br /><br />if&nbsp;(</span><span style="color: #9876AA">ssh2_auth_agent</span><span style="color: #007700">(</span><span style="color: #9876AA">$connection</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'username'</span><span style="color: #007700">))&nbsp;{<br />&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"Authentication&nbsp;Successful!\n"</span><span style="color: #007700">;<br />}&nbsp;else&nbsp;{<br />&nbsp;&nbsp;die(</span><span style="color: #DD0000">'Authentication&nbsp;Failed...'</span><span style="color: #007700">);<br />}<br /></span><span style="color: #9876AA">?&gt;</span> </span> </span></div>     </div>     </div>   </span>  </div>   <div class="up"><a href="http://php.net/manual/zh/ref.ssh2.php">SSH2 函数</a></div></div>
 * @return bool <p>Returns TRUE on success or FALSE on failure.</p>*/
function ssh2_auth_agent ($session, $username) {}

/**
