*/
define ('SSH2_FINGERPRINT_SHA1', 1);

/**
*      Flag to <span class="function">{@link ssh2_fingerprint()}</span> requesting hostkey      fingerprint as a string of hexits.     
*/
define ('SSH2_FINGERPRINT_HEX', 0);

/**
*      Flag to <span class="function">{@link ssh2_fingerprint()}</span> requesting hostkey      fingerprint as a raw string of 8-bit characters.     
*/
define ('SSH2_FINGERPRINT_RAW', 2);

