<?php

// Start of sysvmsg v.

/**
*<div id="function.msg-get-queue" class="refentry">  <div class="refnamediv">   <h1 class="refname">msg_get_queue</h1>   <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">msg_get_queue</span> &mdash; <span class="dc-title">Create or attach to a message queue</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.msg-get-queue-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">resource</span> <span class="methodname" style="color:#CC7832"><strong>msg_get_queue</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$key</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$perms</span><span class="initializer"> = 0666</span></span>   ] )</div>    <p class="para rdfs-comment">    <span class="function"><strong style="color:#CC7832">msg_get_queue()</strong></span> returns an id that can be used to    access the System V message queue with the given    <span class="parameter" style="color:#2EACF9">key</span>. The first call creates the message queue with    the optional <span class="parameter" style="color:#2EACF9">perms</span>.    A second call to <span class="function"><strong style="color:#CC7832">msg_get_queue()</strong></span> for the same    <span class="parameter" style="color:#2EACF9">key</span> will return a different message queue    identifier, but both identifiers access the same underlying message    queue.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.msg-get-queue-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">key</span></dt>       <dd>        <p class="para">        Message queue numeric ID       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">perms</span></dt>       <dd>        <p class="para">        Queue permissions. Default to 0666. If the message queue already        exists, the <span class="parameter" style="color:#2EACF9">perms</span> will be ignored.       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.msg-get-queue-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns a resource handle that can be used to access the System V message queue.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.msg-get-queue-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link msg_remove_queue()} - Destroy a message queue</span></li>     <li class="member"><span class="function">{@link msg_receive()} - Receive a message from a message queue</span></li>     <li class="member"><span class="function">{@link msg_send()} - Send a message to a message queue</span></li>     <li class="member"><span class="function">{@link msg_stat_queue()} - Returns information from the message queue data structure</span></li>     <li class="member"><span class="function">{@link msg_set_queue()} - Set information in the message queue data structure</span></li>    </ul>   </span>  </div>     <div class="up"><a href="http://php.net/manual/zh/ref.sem.php">Semaphore 函数</a></div></div>
 * @return resource a resource handle that can be used to access the System V message queue.*/
function msg_get_queue ($key, $perms = 0666) {}

/**
*<div id="function.msg-send" class="refentry">  <div class="refnamediv">   <h1 class="refname">msg_send</h1>   <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">msg_send</span> &mdash; <span class="dc-title">Send a message to a message queue</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.msg-send-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><strong>msg_send</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$queue</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$msgtype</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.pseudo-types.php#language.types.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#2EACF9">$message</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#2EACF9">$serialize</span><span class="initializer"> = <strong><span>TRUE</span></strong></span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#2EACF9">$blocking</span><span class="initializer"> = <strong><span>TRUE</span></strong></span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">&$errorcode</span></span>   ]]] )</div>    <p class="para rdfs-comment">    <span class="function"><strong style="color:#CC7832">msg_send()</strong></span> sends a <span class="parameter" style="color:#2EACF9">message</span> of type    <span class="parameter" style="color:#2EACF9">msgtype</span> (which MUST be greater than 0) to    the message queue specified by <span class="parameter" style="color:#2EACF9">queue</span>.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.msg-send-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">queue</span></dt>       <dd>        <p class="para">       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">msgtype</span></dt>       <dd>        <p class="para">       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">message</span></dt>       <dd>        <p class="para">       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">serialize</span></dt>       <dd>        <p class="para">        The optional <span class="parameter" style="color:#2EACF9">serialize</span> controls how the        <span class="parameter" style="color:#2EACF9">message</span> is sent.  <span class="parameter" style="color:#2EACF9">serialize</span>        defaults to <strong><span>TRUE</span></strong> which means that the <span class="parameter" style="color:#2EACF9">message</span> is        serialized using the same mechanism as the session module before being        sent to the queue.  This allows complex arrays and objects to be sent to        other PHP scripts, or if you are using the WDDX serializer, to any WDDX        compatible client.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">blocking</span></dt>       <dd>        <p class="para">        If the message is too large to fit in the queue, your script will wait        until another process reads messages from the queue and frees enough        space for your message to be sent.        This is called blocking; you can prevent blocking by setting the        optional <span class="parameter" style="color:#2EACF9">blocking</span> parameter to <strong><span>FALSE</span></strong>, in which        case <span class="function"><strong style="color:#CC7832">msg_send()</strong></span> will immediately return <strong><span>FALSE</span></strong> if the        message is too big for the queue, and set the optional        <span class="parameter" style="color:#2EACF9">errorcode</span> to <strong><span>MSG_EAGAIN</span></strong>,        indicating that you should try to send your message again a little        later on.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">errorcode</span></dt>       <dd>        <p class="para">       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.msg-send-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>   <p class="para">    Upon successful completion the message queue data structure is updated as    follows: <span class="parameter" style="color:#2EACF9">msg_lspid</span> is set to the process-ID of the    calling process, <span class="parameter" style="color:#2EACF9">msg_qnum</span> is incremented by 1 and    <span class="parameter" style="color:#2EACF9">msg_stime</span> is set to the current time.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.msg-send-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link msg_remove_queue()} - Destroy a message queue</span></li>     <li class="member"><span class="function">{@link msg_receive()} - Receive a message from a message queue</span></li>     <li class="member"><span class="function">{@link msg_stat_queue()} - Returns information from the message queue data structure</span></li>     <li class="member"><span class="function">{@link msg_set_queue()} - Set information in the message queue data structure</span></li>    </ul>   </span>  </div>     <div class="up"><a href="http://php.net/manual/zh/ref.sem.php">Semaphore 函数</a></div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function msg_send ($queue, $msgtype, $message, $serialize = true, $blocking = true, &$errorcode = null) {}

/**
*<div id="function.msg-receive" class="refentry">  <div class="refnamediv">   <h1 class="refname">msg_receive</h1>   <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">msg_receive</span> &mdash; <span class="dc-title">Receive a message from a message queue</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.msg-receive-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><strong>msg_receive</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$queue</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$desiredmsgtype</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">&$msgtype</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$maxsize</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.pseudo-types.php#language.types.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#2EACF9">&$message</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#2EACF9">$unserialize</span><span class="initializer"> = <strong><span>TRUE</span></strong></span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$flags</span><span class="initializer"> = 0</span></span>    [, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">&$errorcode</span></span>   ]]] )</div>    <p class="para rdfs-comment">    <span class="function"><strong style="color:#CC7832">msg_receive()</strong></span> will receive the first message from the    specified <span class="parameter" style="color:#2EACF9">queue</span> of the type specified by    <span class="parameter" style="color:#2EACF9">desiredmsgtype</span>.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.msg-receive-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">queue</span></dt>       <dd>        <p class="para">       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">desiredmsgtype</span></dt>       <dd>        <p class="para">        If <span class="parameter" style="color:#2EACF9">desiredmsgtype</span> is 0, the message from the front        of the queue is returned. If <span class="parameter" style="color:#2EACF9">desiredmsgtype</span> is        greater than 0, then the first message of that type is returned.        If <span class="parameter" style="color:#2EACF9">desiredmsgtype</span> is less than 0, the first        message on the queue with a type less than or equal to the        absolute value of <span class="parameter" style="color:#2EACF9">desiredmsgtype</span> will be read.        If no messages match the criteria, your script will wait until a suitable        message arrives on the queue.  You can prevent the script from blocking        by specifying <strong><span>MSG_IPC_NOWAIT</span></strong> in the        <span class="parameter" style="color:#2EACF9">flags</span> parameter.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">msgtype</span></dt>       <dd>        <p class="para">        The type of the message that was received will be stored in this        parameter.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">maxsize</span></dt>       <dd>        <p class="para">        The maximum size of message to be accepted is specified by the        <span class="parameter" style="color:#2EACF9">maxsize</span>; if the message in the queue is larger        than this size the function will fail (unless you set        <span class="parameter" style="color:#2EACF9">flags</span> as described below).       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">message</span></dt>       <dd>        <p class="para">        The received message will be stored in <span class="parameter" style="color:#2EACF9">message</span>,        unless there were errors receiving the message.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">unserialize</span></dt>       <dd>        <p class="para">        If set to        <strong><span>TRUE</span></strong>, the message is treated as though it was serialized using the        same mechanism as the session module. The message will be unserialized        and then returned to your script. This allows you to easily receive        arrays or complex object structures from other PHP scripts, or if you        are using the WDDX serializer, from any WDDX compatible source.       </p>       <p class="para">        If <span class="parameter" style="color:#2EACF9">unserialize</span> is <strong><span>FALSE</span></strong>, the message will be        returned as a binary-safe string.       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">flags</span></dt>       <dd>        <p class="para">        The optional <span class="parameter" style="color:#2EACF9">flags</span> allows you to pass flags to the        low-level msgrcv system call.  It defaults to 0, but you may specify one        or more of the following values (by adding or ORing them together).        <table class="doctable table">         <caption><strong>Flag values for msg_receive</strong></caption>                   <tbody class="tbody">           <tr>            <td><strong><span>MSG_IPC_NOWAIT</span></strong></td>            <td>If there are no messages of the             <span class="parameter" style="color:#2EACF9">desiredmsgtype</span>, return immediately and do not             wait.  The function will fail and return an integer value             corresponding to <strong><span>MSG_ENOMSG</span></strong>.            </td>           </tr>            <tr>            <td><strong><span>MSG_EXCEPT</span></strong></td>            <td>Using this flag in combination with a             <span class="parameter" style="color:#2EACF9">desiredmsgtype</span> greater than 0 will cause the             function to receive the first message that is not equal to             <span class="parameter" style="color:#2EACF9">desiredmsgtype</span>.</td>           </tr>            <tr>            <td><strong><span>MSG_NOERROR</span></strong></td>            <td>             If the message is longer than <span class="parameter" style="color:#2EACF9">maxsize</span>,             setting this flag will truncate the message to             <span class="parameter" style="color:#2EACF9">maxsize</span> and will not signal an error.            </td>           </tr>           </tbody>                 </table>        </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">errorcode</span></dt>       <dd>        <p class="para">        If the function fails, the optional <span class="parameter" style="color:#2EACF9">errorcode</span>        will be set to the value of the system errno variable.       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.msg-receive-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>   <p class="para">    Upon successful completion the message queue data structure is updated as    follows: <em>msg_lrpid</em> is set to the process-ID of the    calling process, <em>msg_qnum</em> is decremented by 1 and    <em>msg_rtime</em> is set to the current time.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.msg-receive-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link msg_remove_queue()} - Destroy a message queue</span></li>     <li class="member"><span class="function">{@link msg_send()} - Send a message to a message queue</span></li>     <li class="member"><span class="function">{@link msg_stat_queue()} - Returns information from the message queue data structure</span></li>     <li class="member"><span class="function">{@link msg_set_queue()} - Set information in the message queue data structure</span></li>    </ul>   </span>  </div>     <div class="up"><a href="http://php.net/manual/zh/ref.sem.php">Semaphore 函数</a></div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function msg_receive ($queue, $desiredmsgtype, &$msgtype, $maxsize, &$message, $unserialize = true, $flags = 0, &$errorcode = null) {}

/**
*<div id="function.msg-remove-queue" class="refentry">  <div class="refnamediv">   <h1 class="refname">msg_remove_queue</h1>   <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">msg_remove_queue</span> &mdash; <span class="dc-title">Destroy a message queue</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.msg-remove-queue-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><strong>msg_remove_queue</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$queue</span></span>    )</div>    <p class="para rdfs-comment">    <span class="function"><strong style="color:#CC7832">msg_remove_queue()</strong></span> destroys the message queue specified    by the <span class="parameter" style="color:#2EACF9">queue</span>.  Only use this function when all    processes have finished working with the message queue and you need to    release the system resources held by it.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.msg-remove-queue-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">queue</span></dt>       <dd>        <p class="para">        Message queue resource handle       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.msg-remove-queue-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.msg-remove-queue-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link msg_get_queue()} - Create or attach to a message queue</span></li>     <li class="member"><span class="function">{@link msg_receive()} - Receive a message from a message queue</span></li>     <li class="member"><span class="function">{@link msg_stat_queue()} - Returns information from the message queue data structure</span></li>     <li class="member"><span class="function">{@link msg_set_queue()} - Set information in the message queue data structure</span></li>    </ul>   </span>  </div>     <div class="up"><a href="http://php.net/manual/zh/ref.sem.php">Semaphore 函数</a></div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function msg_remove_queue ($queue) {}

/**
*<div id="function.msg-stat-queue" class="refentry">  <div class="refnamediv">   <h1 class="refname">msg_stat_queue</h1>   <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">msg_stat_queue</span> &mdash; <span class="dc-title">Returns information from the message queue data structure</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.msg-stat-queue-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">array</span> <span class="methodname" style="color:#CC7832"><strong>msg_stat_queue</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$queue</span></span>    )</div>    <p class="para rdfs-comment">    <span class="function"><strong style="color:#CC7832">msg_stat_queue()</strong></span> returns the message queue meta data    for the message queue specified by the <span class="parameter" style="color:#2EACF9">queue</span>.    This is useful, for example, to determine which process sent the message    that was just received.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.msg-stat-queue-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">queue</span></dt>       <dd>        <p class="para">        Message queue resource handle       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.msg-stat-queue-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    The return value is an array whose keys and values have the following    meanings:    <table class="doctable table">     <caption><strong>Array structure for msg_stat_queue</strong></caption>           <tbody class="tbody">       <tr>        <td><em>msg_perm.uid</em></td>        <td>         The uid of the owner of the queue.        </td>       </tr>        <tr>        <td><em>msg_perm.gid</em></td>        <td>         The gid of the owner of the queue.        </td>       </tr>        <tr>        <td><em>msg_perm.mode</em></td>        <td>         The file access mode of the queue.        </td>       </tr>        <tr>        <td><em>msg_stime</em></td>        <td>         The time that the last message was sent to the queue.        </td>       </tr>        <tr>        <td><em>msg_rtime</em></td>        <td>         The time that the last message was received from the queue.        </td>       </tr>        <tr>        <td><em>msg_ctime</em></td>        <td>         The time that the queue was last changed.        </td>       </tr>        <tr>        <td><em>msg_qnum</em></td>        <td>         The number of messages waiting to be read from the queue.        </td>       </tr>        <tr>        <td><em>msg_qbytes</em></td>        <td>         The maximum number of bytes allowed in one message queue. On         Linux, this value may be read and modified via         <var class="filename">/proc/sys/kernel/msgmnb</var>.        </td>       </tr>        <tr>        <td><em>msg_lspid</em></td>        <td>         The pid of the process that sent the last message to the queue.        </td>       </tr>        <tr>        <td><em>msg_lrpid</em></td>        <td>         The pid of the process that received the last message from the queue.        </td>       </tr>       </tbody>         </table>    </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.msg-stat-queue-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link msg_remove_queue()} - Destroy a message queue</span></li>     <li class="member"><span class="function">{@link msg_receive()} - Receive a message from a message queue</span></li>     <li class="member"><span class="function">{@link msg_get_queue()} - Create or attach to a message queue</span></li>     <li class="member"><span class="function">{@link msg_set_queue()} - Set information in the message queue data structure</span></li>    </ul>   </span>  </div>     <div class="up"><a href="http://php.net/manual/zh/ref.sem.php">Semaphore 函数</a></div></div>
 * @return array The return value is an array whose keys and values have the following*/
function msg_stat_queue ($queue) {}

/**
*<div id="function.msg-set-queue" class="refentry">  <div class="refnamediv">   <h1 class="refname">msg_set_queue</h1>   <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7)</p><p class="refpurpose"><span class="refname">msg_set_queue</span> &mdash; <span class="dc-title">Set information in the message queue data structure</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.msg-set-queue-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><strong>msg_set_queue</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#2EACF9">$queue</span></span>    , <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#2EACF9">$data</span></span>    )</div>    <p class="para rdfs-comment">    <span class="function"><strong style="color:#CC7832">msg_set_queue()</strong></span> allows you to change the values of the    msg_perm.uid, msg_perm.gid, msg_perm.mode and msg_qbytes fields of the    underlying message queue data structure.   </p>   <p class="para">    Changing the data structure will require that PHP be running as the same    user that created the queue, owns the queue (as determined by the    existing msg_perm.xxx fields), or be running with root privileges.    root privileges are required to raise the msg_qbytes values above the    system defined limit.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.msg-set-queue-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">queue</span></dt>       <dd>        <p class="para">        Message queue resource handle       </p>      </dd>                 <dt> <span class="parameter" style="color:#2EACF9">data</span></dt>       <dd>        <p class="para">        You specify the values you require by setting the value of the keys        that you require in the <span class="parameter" style="color:#2EACF9">data</span> array.       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.msg-set-queue-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.msg-set-queue-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link msg_remove_queue()} - Destroy a message queue</span></li>     <li class="member"><span class="function">{@link msg_receive()} - Receive a message from a message queue</span></li>     <li class="member"><span class="function">{@link msg_stat_queue()} - Returns information from the message queue data structure</span></li>     <li class="member"><span class="function">{@link msg_get_queue()} - Create or attach to a message queue</span></li>    </ul>   </span>  </div>     <div class="up"><a href="http://php.net/manual/zh/ref.sem.php">Semaphore 函数</a></div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function msg_set_queue ($queue, array $data) {}

/**
*<div id="function.msg-queue-exists" class="refentry">  <div class="refnamediv">   <h1 class="refname">msg_queue_exists</h1>   <p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7)</p><p class="refpurpose"><span class="refname">msg_queue_exists</span> &mdash; <span class="dc-title">Check whether a message queue exists</span></p>   </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.msg-queue-exists-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="type" style="color:#EAB766">bool</span> <span class="methodname" style="color:#CC7832"><strong>msg_queue_exists</strong></span>     ( <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#2EACF9">$key</span></span>    )</div>    <p class="para rdfs-comment">    Checks whether the message queue <span class="parameter" style="color:#2EACF9">key</span> exists.   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.msg-queue-exists-parameters">   <h3 class="title">参数</h3>   <span>    <dl>            <dt> <span class="parameter" style="color:#2EACF9">key</span></dt>       <dd>        <p class="para">        Queue key.       </p>      </dd>          </dl>    </span>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.msg-queue-exists-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    成功时返回 <strong><span>TRUE</span></strong>， 或者在失败时返回 <strong><span>FALSE</span></strong>。   </p>  </div>    <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.msg-queue-exists-seealso">   <h3 class="title">参见</h3>   <span>    <ul class="simplelist">     <li class="member"><span class="function">{@link msg_remove_queue()} - Destroy a message queue</span></li>     <li class="member"><span class="function">{@link msg_receive()} - Receive a message from a message queue</span></li>     <li class="member"><span class="function">{@link msg_stat_queue()} - Returns information from the message queue data structure</span></li>    </ul>   </span>  </div>     <div class="up"><a href="http://php.net/manual/zh/ref.sem.php">Semaphore 函数</a></div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
